-- Dialogue: canon-narrative-dialogue-kaede-ishikawa
INSERT INTO narrative.dialogue_nodes (dialogue_id, title, content_data, version, is_active) VALUES ('canon-narrative-dialogue-kaede-ishikawa', 'Диалоги — Каэдэ Исикава', '{"metadata": {"id": "canon-narrative-dialogue-kaede-ishikawa", "title": "Диалоги — Каэдэ Исикава", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.3.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-07T22:04:00+00:00", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["dialogue", "specter", "helios"], "topics": ["narrative", "raids"], "related_systems": ["narrative-service", "world-service", "social-service"], "related_documents": [{"id": "canon-npc-kaede-ishikawa", "relation": "references"}, {"id": "canon-quests-raid-helios-countermesh-conspiracy", "relation": "references"}, {"id": "canon-gameplay-world-specter-hq", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/dialogues/npc-kaede-ishikawa.md", "visibility": "internal", "audience": ["narrative", "systems-design", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "Raid Narrative Cell", "reviewed_at": "2025-11-07T22:04:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Диалог Каэдэ Исикавы существовал в Markdown и не был интегрирован в стандарт знаний по многостадийным веткам Specter vs Helios.", "goal": "Структурировать состояния, проверки и событийные реакции двойного агента для синхронизации с narrative-service и world-service.", "essence": "Документ описывает ветки лояльности Specter, Helios и баланс, кризис семьи и медиаторские операции Underlink с привязкой к событиям города.", "key_points": ["Состояния neutral, specter, helios, balanced, helios-agent, underlink-mediator и family_crisis управляются флагами лояльности и событиями.", "Узлы включают доступ к Countermesh логам, контракты Specter, операции Helios CM-Viper и совместный Underlink mediator.", "Проверки Hacking, Persuasion, Insight и Leadership определяют переходы лояльности, запускают мировые события и изменяют city_unrest."]}, "content": {"sections": [{"id": "context", "title": "Контекст и тон", "body": "Каэдэ Исикава выступает двойным агентом, соединяющим рейд Helios Countermesh с операциями Specter HQ. Диалог\\nреагирует на состояние города, war-meter и семейные угрозы, сохраняя пасхалки на события кибервойн 2020-х.\\n", "mechanics_links": ["../../02-gameplay/world/helios-countermesh-ops.md"], "assets": []}, {"id": "states", "title": "Состояния и переходы", "body": "Состояния neutral, specter, helios, balanced, helios-agent, underlink-mediator и family_crisis активируются по\\nфлагам лояльности (`flag.kaede.loyalty:*`), событиям (`HELIOS_SPECTER_PROXY_WAR`, `FAMILY_RESCUE_MISSION`) и\\nрепутационным условиям. Семейный кризис переводит Kaede в emergency-выполнение.\\n", "mechanics_links": ["../../02-gameplay/social/reputation-formulas.md"], "assets": []}, {"id": "dialogue_nodes", "title": "Ключевые узлы диалога", "body": "Ветви `neutral-entry`, `specter-briefing`, `helios-oath`, `balanced-dialogue`, `helios-agent-brief`,\\n`mediator-operations` и `family-crisis-node` описывают опции игрока, требования проверок, выдачу контрактов,\\nизменение флагов и запуск событий. Узел `family_crisis` управляет спасением родственников и резким ростом unrest.\\n", "mechanics_links": [], "assets": []}, {"id": "checks", "title": "Проверки и последствия", "body": "Проверки Hacking, Persuasion, Insight, Leadership, Deception и Streetwise (пункт `hint-evergiven`) задают DC,\\nмодификаторы и критические исходы. Успехи открывают контракты (`cm-viper-raid`, `underlink-mediator-run`),\\nпровалы повышают `city_unrest` и запускают ответные события Helios.\\n", "mechanics_links": [], "assets": []}, {"id": "events", "title": "Событийные реакции", "body": "Диалог реагирует на `HELIOS_CONSPIRACY_ALLY`, `SPECTER_CONSPIRACY_EXPOSED`, `HELIOS_SPECTER_PROXY_WAR` и\\n`FAMILY_RESCUE_MISSION_SUCCESS`, обновляя состояние Kaede, war-meter, доступ к активностям и пасхальные медиа-фиды.\\n", "mechanics_links": ["../../02-gameplay/world/events/world-events-framework.md"], "assets": []}, {"id": "rewards", "title": "Награды и активности", "body": "Лояльность Specter открывает `specter-intel-contract` и `specter-family-cache`, Helios — `helios-blackwall-signet`\\nи рейд `cm-viper-raid`, баланс — `underlink-mediator-run`. Провал спасения семьи активирует событие\\n«Memorial Blackout» с общественным осуждением Helios.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Helios Countermesh Quest", "link": "../../../content/quests/raid/2025-11-07-quest-helios-countermesh-conspiracy.md"}, {"title": "Specter HQ Overview", "link": "../../02-gameplay/world/specter-hq.md"}, {"title": "Helios Operations Brief", "link": "../../02-gameplay/world/helios-countermesh-ops.md"}], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 107, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.3.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертирован диалог Каэдэ Исикавы в YAML с описанием состояний лояльности, проверок и событий рейда."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (dialogue_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

