-- Dialogue: canon-narrative-dialogue-quest-fq-arasaka-001
INSERT INTO narrative.dialogue_nodes (dialogue_id, title, content_data, version, is_active) VALUES ('canon-narrative-dialogue-quest-fq-arasaka-001', 'Диалоги — FQ-ARASAKA-001 «Токийская штаб-квартира»', '{"metadata": {"id": "canon-narrative-dialogue-quest-fq-arasaka-001", "title": "Диалоги — FQ-ARASAKA-001 «Токийская штаб-квартира»", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-07T19:32:00+00:00", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["dialogue", "faction", "arasaka"], "topics": ["narrative", "corporate"], "related_systems": ["narrative-service", "world-service", "social-service"], "related_documents": [{"id": "canon-quests-faction-world-arasaka", "relation": "references"}, {"id": "canon-narrative-dialogue-hiroshi-tanaka", "relation": "complements"}, {"id": "canon-narrative-dialogue-james-iron-reed", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/dialogues/quest-faction-arasaka-001-tokyo.md", "visibility": "internal", "audience": ["narrative", "systems-design", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "Corporate Narrative Cell", "reviewed_at": "2025-11-07T19:32:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Сценарий диалога миссии FQ-ARASAKA-001 находился в Markdown и не был структурирован для экспорта в narrative-service и корпоративные трекеры.", "goal": "Формализовать состояния, проверки и флаги лояльности/предательства, чтобы поддержать интеграцию с системами Arasaka и Militech.", "essence": "YAML фиксирует брифинг в Токио, охранные проверки, искушение вскрыть кейс, предложения Militech и финальную передачу в Night City.", "key_points": ["Состояния briefing, transit, temptation, betrayal и extraction управляются флагами `flag.fqara001.*` и прогрессом миссии.", {"Узлы описывают выборы игрока": "подтверждение лояльности, стелс на контроле, вскрытие кейса, сделку с Militech и отчёт Hiroshi."}, "Проверки Persuasion, Stealth, Hacking, Willpower, Negotiation и Deception меняют репутацию корпораций и запускают double-agent ветки."]}, "content": {"sections": [{"id": "context", "title": "Контекст миссии", "body": "Игрок получает первый международный контракт Arasaka в токийской штаб-квартире, доставляет кейс в Night City и\\nсталкивается с выбором между лояльностью и предательством. Сцена задействует Hiroshi Tanaka, охрану Arasaka и\\nконтакты Militech.\\n", "mechanics_links": ["../../02-gameplay/social/reputation-formulas.md"], "assets": []}, {"id": "states", "title": "Состояния и флаги", "body": "Состояния briefing, transit, temptation, betrayal и extraction зависят от прогресса квеста и флагов `flag.fqara001.*`.\\nМировое событие `world.event.corporate_war_escalation` увеличивает DC охраны и финальной отчётности.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue_flow", "title": "Основные узлы", "body": "Узлы `briefing`, `security-gate`, `temptation`, `betrayal`, `extraction` и `night-flight` задают реплики NPC и\\nответы игрока, включая создание safehouse, вскрытие кейса и переговоры с James Reed.\\n", "mechanics_links": [], "assets": []}, {"id": "checks", "title": "Проверки и последствия", "body": "Проверки Persuasion, Stealth, Hacking, Willpower, Negotiation и Deception управляют доступом к safehouse,\\nфлагами лояльности, сделками Militech и риском blacklist. Критические успехи дают VIP-пропуски, провалы вызывают\\nмаяки отслеживания и штрафы.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Ветви и последствия", "body": "Лояльность (`flag.fqara001.success`) открывает дальнейшие миссии Arasaka, предательство (`flag.fqara001.betray`)\\nусиливает Militech, а двойной агент (`flag.fqara001.double_agent`) активирует секретные ветки обеих корпораций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Faction Quest Catalog", "link": "../quests/faction-world/arasaka-world-quests.md"}, {"title": "Hiroshi Tanaka Dialogue", "link": "../dialogues/npc-hiroshi-tanaka.yaml"}, {"title": "James Iron Reed Dialogue", "link": "../dialogues/npc-james-iron-reed.yaml"}], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 107, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Диалог миссии FQ-ARASAKA-001 перенесён в YAML, структурированы ветки лояльности и предательства."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (dialogue_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

