INSERT INTO narrative.dialogue_nodes (dialogue_id, title, content_data, version, is_active)
VALUES ('canon-narrative-dialogue-blood-brothers', 'Диалоги — Blood Brothers (медицинская банда)',
        '{"metadata": {"id": "canon-narrative-dialogue-blood-brothers", "title": "Диалоги — Blood Brothers (медицинская банда)", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-20T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-20T00:00:00+00:00", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["dialogue", "faction", "medical", "blood-trade"], "topics": ["narrative", "medical", "faction-interaction"], "related_systems": ["narrative-service", "medical-service", "faction-service"], "related_documents": [{"id": "canon-lore-factions-gangs-blood-brothers-night-city-2035-2093", "relation": "references"}, {"id": "canon-mechanics-medical-blood-economy", "relation": "implements"}, {"id": "canon-mechanics-social-reputation-blood-brothers", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/dialogues/faction-blood-brothers.md", "visibility": "internal", "audience": ["narrative", "content", "game-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "Medical Lore Review Cell", "reviewed_at": "2025-12-20T00:00:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Blood Brothers как уникальная медицинская фракция нуждалась в диалогах, отражающих их философию \\"кровь = валюта\\".", "goal": "Создать диалоговую систему, интегрирующую торговлю кровью, медицинские услуги и фракционные взаимодействия.", "essence": "Диалоги Blood Brothers сочетают профессиональную медицину с уличной этикой, где кровь и органы становятся валютой доверия.", "key_points": ["Диалоги включают торговлю кровью, медицинские консультации и фракционные задания", "Проверки влияют на медицинское состояние игрока и репутацию фракции", "Этические дилеммы между спасением жизней и прибылью", "Различные состояния для разных уровней отношений с фракцией"]}, "content": {"sections": [{"id": "context", "title": "Контекст и роль Blood Brothers", "body": "Blood Brothers - подпольная медицинская фракция Night City, торгующая кровью, органами и экспериментальными лечениями.\\nОни позиционируют себя как \\"альтернативную медицину\\" для тех, кого отвергла система. Диалог отражает их\\nфилософию: \\"Кровь - это жизнь, жизнь - это власть\\".\\n", "mechanics_links": ["../../02-gameplay/medical/blood-economy-system.md"], "assets": []}, {"id": "states", "title": "Состояния и триггеры", "body": "**outsider**: Начальный контакт. Доступны базовые услуги и информация.\\n\\n**donor**: После первой сдачи крови или помощи. Открывает скидки и дополнительные услуги.\\n\\n**trusted-patient**: Регулярные взаимодействия. Доступны экспериментальные лечения.\\n\\n**crisis-patient**: Во время медицинских кризисов. Экстренные услуги и стабилизация.\\n\\n**debt-collector**: При долгах фракции. Высокое давление и угрозы.\\n\\n**marked-target**: Вражда с фракцией. Ограниченный доступ, повышенные цены.\\n", "mechanics_links": ["../../02-gameplay/social/reputation-blood-brothers.md"], "assets": []}, {"id": "dialogue_flow", "title": "Узлы и ключевые опции", "body": "**blood-assessment**: Оценка крови игрока и предложения по донорству.\\n\\n**medical-services**: Лечение ран, установка имплантов, стабилизация психоза.\\n\\n**organ-trade**: Покупка/продажа органов на черном рынке.\\n\\n**faction-jobs**: Задания по сбору крови, защите клиник, устранению конкурентов.\\n\\n**ethical-dilemmas**: Выбор между прибылью фракции и спасением жизней.\\n", "mechanics_links": [], "assets": []}, {"id": "checks", "title": "Проверки и исходы", "body": "**Medical Knowledge (DC 15)**: Понимание медицинских процедур. Влияет на качество лечения.\\n\\n**Streetwise (DC 16)**: Знание подпольного рынка. Открывает лучшие сделки.\\n\\n**Intimidation (DC 17)**: Давление на фракцию. Риск конфликта.\\n\\n**Persuasion (DC 14)**: Убеждение в сотрудничестве. Повышает репутацию.\\n\\n**Willpower (DC 18)**: Сопротивление манипуляциям. Защищает от скрытых эффектов.\\n\\n**Empathy (DC 16)**: Проявление заботы. Улучшает отношения с лидерами.\\n", "mechanics_links": [], "assets": []}, {"id": "events", "title": "Реакции на внешние события", "body": "**medical-crisis**: Эпидемии или массовые травмы. Фракция предлагает помощь за плату.\\n\\n**organ-shortage**: Дефицит органов. Повышенные цены и черный рынок.\\n\\n**police-raid**: Рейды NCPD. Фракция ищет защиту или союзников.\\n\\n**competitor-conflict**: Столкновения с Trauma Team. Предложения о сотрудничестве или войне.\\n\\n**player-injury**: Игрок ранен. Автоматическая активация emergency response.\\n", "mechanics_links": ["../../02-gameplay/world/events/medical-events-framework.md"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "**Medical Benefits**: Лечение ран, стабилизация психоза, экспериментальные импланты.\\n\\n**Blood Credits**: Валюта фракции для скидок на услуги.\\n\\n**Faction Items**: Редкие медикаменты, протезы, стабилизаторы.\\n\\n**Reputation Boosts**: Повышение rep.blood-brothers и доступ к эксклюзивным услугам.\\n\\n**Quest Opportunities**: Медицинские задания, сбор крови, защита территорий.\\n\\n**Ethical Choices**: Влияние на мировоззрение и отношения с другими фракциями.\\n", "mechanics_links": [], "assets": []}]}, "dialogue_nodes": [{"id": "first_contact_assessment", "trigger": "state.outsider", "npc_text": "\\"Добро пожаловать в нашу клинику, пациент. Я - Sister Mercy. Мы Blood Brothers -\\nте, кто понимает, что кровь - это самая ценная валюта в Night City.\\nЧто привело вас к нам? Нужна ли помощь... или у вас есть что предложить?\\"\\n", "player_options": [{"text": "Мне нужно лечение. Деньги не проблема.", "check": "persuasion(14)", "success": {"npc_response": "Отлично. Давайте посмотрим, что можно сделать. У вас редкая группа крови...", "outcome": "medical_consultation"}, "failure": {"npc_response": "Деньги - это кровь. Покажите свою готовность... пожертвовать.", "outcome": "blood_donation_offer"}}, {"text": "Я слышал, вы торгуете органами.", "check": "streetwise(16)", "success": {"npc_response": "Тихо! Такие разговоры опасны. Но... да, мы можем обсудить возможности.", "outcome": "organ_trade_menu"}, "failure": {"npc_response": "Вы не понимаете, о чем говорите. Уходите, пока целы.", "outcome": "hostile_exit"}}]}, {"id": "donor_relationship_building", "trigger": "state.donor", "npc_text": "\\"Ах, наш постоянный донор вернулся. Ваша кровь... она особенная. Мы высоко ценим вашу щедрость.\\nЧто сегодня? Ещё одна сдача? Или, может, вы хотите воспользоваться нашими услугами?\\"\\n", "player_options": [{"text": "Расскажите о вашей философии.", "check": "insight(15)", "success": {"npc_response": "\\"Кровь - это жизнь, пациент. В этом городе жизнь дешева. Мы даем ей ценность.\\nКаждый литр, каждый орган - это вклад в большую экономику. Мы не просто врачи... мы банкиры жизни.\\"\\n", "outcome": "philosophy_unlock"}, "failure": {"npc_response": "Философия - для тех, кто жертвует достаточно крови.", "outcome": "neutral_response"}}, {"text": "У меня есть друг, нуждающийся в помощи.", "check": "empathy(16)", "success": {"npc_response": "Забота о близких... Это делает вас человеком. Мы поможем. Но помните о цене.", "outcome": "referral_discount"}, "failure": {"npc_response": "Друзья - это слабость в Night City. Но мы можем это исправить.", "outcome": "manipulation_attempt"}}]}, {"id": "trusted_patient_experimental", "trigger": "state.trusted-patient", "npc_text": "\\"Доктор Carver будет рад вас видеть. Вы стали одним из наших самых ценных пациентов.\\nУ нас есть... экспериментальные процедуры. Готовы ли вы к следующему шагу эволюции?\\"\\n", "player_options": [{"text": "Что за эксперименты?", "outcome": "experimental_menu"}, {"text": "Я слышал о ваших ''ритуалах''.", "check": "willpower(17)", "success": {"npc_response": "Ах, вы любопытны. Наши ритуалы... Они объединяют доноров в семью. Кровь за кровь.", "outcome": "ritual_invitation"}, "failure": {"npc_response": "Некоторые вещи лучше не знать. Для вашей же безопасности.", "outcome": "secret_denial"}}]}, {"id": "crisis_emergency_response", "trigger": "state.crisis-patient", "npc_text": "\\"Вы в плохом состоянии, пациент. Кровотечение... признаки шока. Мы можем стабилизировать,\\nно цена высока. Ваша жизнь или... чья-то другая? Выбор за вами.\\"\\n", "player_options": [{"text": "Спасите меня! Деньги не проблема.", "outcome": "emergency_treatment"}, {"text": "Я могу заплатить кровью. Своей или чужой.", "check": "intimidation(18)", "success": {"npc_response": "Хорошо. Мы возьмем то, что нужно. Ваша решимость впечатляет.", "outcome": "blood_payment_deal"}, "failure": {"npc_response": "Такие решения не принимаются под давлением. Уходите.", "outcome": "treatment_denied"}}]}, {"id": "debt_collection_warning", "trigger": "state.debt-collector", "npc_text": "\\"У вас долги перед нами, пациент. Кровь, которую вы брали взаймы... пора возвращать.\\nМы можем договориться... или забрать то, что причитается.\\"\\n", "player_options": [{"text": "Я верну долг. Дайте время.", "check": "persuasion(16)", "success": {"npc_response": "Время - это кровь. Но мы подождем... пока.", "outcome": "extension_granted"}, "failure": {"npc_response": "Время вышло. Пора платить.", "outcome": "debt_enforcement"}}, {"text": "Вы не получите от меня ничего!", "check": "intimidation(19)", "success": {"npc_response": "Хорошо. Но помните: в Night City все имеют цену.", "outcome": "temporary_truce"}, "failure": {"npc_response": "Это была ошибка. Мы найдём способ взыскать долг.", "outcome": "faction_hostility"}}]}], "appendix": {"glossary": [{"term": "Blood Brothers", "definition": "Медицинская фракция Night City, торгующая кровью и органами"}, {"term": "Life Debt", "definition": "Система долгов за медицинские услуги Blood Brothers"}, {"term": "Blood Economy", "definition": "Экономическая система, где кровь служит валютой"}, {"term": "Harvest Ritual", "definition": "Церемония сбора крови и принятия новых членов фракции"}], "references": [{"title": "Blood Brothers Gang Lore", "link": "../../factions/gangs/blood-brothers-night-city-2035-2093.yaml"}, {"title": "Blood Economy System", "link": "../../../mechanics/economy/blood-economy-system.yaml"}, {"title": "Medical Reputation System", "link": "../../../mechanics/social/medical-reputation-system.yaml"}], "decisions": []}, "implementation": {"github_issue": "???", "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-20", "author": "content_director", "changes": "Созданы комплексные диалоги для Blood Brothers с медицинской этикой и торговлей кровью."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb,
        1, true) ON CONFLICT (dialogue_id) DO
