INSERT INTO narrative.dialogue_nodes (dialogue_id, title, content_data, version, is_active) VALUES ('canon-narrative-dialogues-faction-social-lines', 'Faction Social Lines — лидеры и отношения', '{"metadata": {"id": "canon-narrative-dialogues-faction-social-lines", "title": "Faction Social Lines — лидеры и отношения", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-08T12:20:00+00:00", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["dialogue", "factions"], "topics": ["social", "narrative"], "related_systems": ["social-service", "narrative-service", "romance-service"], "related_documents": [{"id": "canon-narrative-dialogue-template", "relation": "complements"}, {"id": "canon-vision-unique-features", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/dialogues/faction-social-lines.md", "visibility": "internal", "audience": ["narrative", "systems-design", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "Brain Manager", "reviewed_at": "2025-11-08T12:20:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Социальные линии лидеров фракций были хранены в Markdown и не синхронизировались с сервисами.", "goal": "Консолидировать мотивацию лидеров, условия доступа и технические интерфейсы для их диалогов.", "essence": "Документ описывает ветки отношений фракционных лидеров, репутационные условия, API-контуры и схемы данных.", "key_points": ["Таблица лидеров охватывает Aeon Dynasty, Crescent Energy, Mnemosyne, Ember Saints, Void Sirens и др.", "Пример Amira Al-Faris демонстрирует развилки (романтика, деловой союз) и влияния на экономику.", "Прописаны REST/WS интерфейсы и схемы БД для отслеживания прогресса диалогов."]}, "content": {"sections": [{"id": "overview", "title": "Обзор и назначение", "body": "Social Lines фиксируют лидерские ветки фракций, их мотивацию и тип связи с игроком (романтическая, прагматичная,\\nфилософская). Каждая линия привязана к репутации, контрактам и событиям мира.\\n", "mechanics_links": [], "assets": []}, {"id": "leaders", "title": "Лидеры и условия доступа", "body": "Таблица лидеров задаёт минимальные значения репутации, сюжетные требования и ключевые темы. Примеры включают Aeon\\nDynasty (Liang Celestial Wen), Crescent Energy (Amira Al-Faris) и Echo Dominion (Arbiter Z3N).\\n", "mechanics_links": [], "assets": []}, {"id": "example_line", "title": "Пример ветки Amira Al-Faris", "body": "Ветка содержит инициацию после Desert Grid, делится на романтический и деловой маршруты, использует пороги\\naffinity.crescent и влияет на цены энергии и транспорт.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue_nodes", "title": "Узлы и типовые ветки", "body": "Узлы включают rust_test (репутационный аудит), confession_branch (романтический выбор), pact_branch (деловой\\nсоюз) и history_reflection (исторические отсылки). Каждый узел взаимодействует с economy-service и world events.\\n", "mechanics_links": [], "assets": []}, {"id": "tech_integration", "title": "Техническая интеграция", "body": "REST/WS интерфейсы охватывают выбор ветки, прогресс и нотификации. SQL структуры faction_dialogues и\\nfaction_dialogue_progress обеспечивают хранение узлов и состояния игроков.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Social Dialogues API", "link": "/social/dialogues"}, {"title": "Reputation Formulas", "link": "../../02-gameplay/social/reputation-formulas.md"}], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 107, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Социальные линии лидеров фракций перенесены в YAML с таблицами и интеграциями."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (dialogue_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

