-- Dialogue: canon-narrative-dialogue-metalheads
INSERT INTO narrative.dialogue_nodes (dialogue_id, title, content_data, version, is_active) VALUES ('canon-narrative-dialogue-metalheads', 'Диалоги — Metalheads (киберпсихо банда)', '{"metadata": {"id": "canon-narrative-dialogue-metalheads", "title": "Диалоги — Metalheads (киберпсихо банда)", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-20T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-20T00:00:00+00:00", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["dialogue", "faction", "cyberpsycho", "metalheads"], "topics": ["narrative", "cyberpsychosis", "faction-interaction"], "related_systems": ["narrative-service", "combat-service", "mental-health-service"], "related_documents": [{"id": "canon-lore-factions-gangs-metalheads-night-city-2045-2093", "relation": "references"}, {"id": "canon-mechanics-combat-cyberpsychosis", "relation": "implements"}, {"id": "canon-mechanics-mental-health-psychosis-stages", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/dialogues/faction-metalheads.md", "visibility": "internal", "audience": ["narrative", "content", "game-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "Cyberpsycho Lore Review Cell", "reviewed_at": "2025-12-20T00:00:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Metalheads как фракция киберпсихопатов нуждалась в диалогах, отражающих их хаотичную психику и философию \\"больше хрома\\".", "goal": "Создать диалоговую систему с элементами безумия, где разговоры отражают стадию психоза и уровень имплантов.", "essence": "Диалоги Metalheads сочетают моменты ясности с приступами безумия, где боль и технологии становятся путем к \\"просветлению\\".", "key_points": ["Диалоги адаптируются под стадию cyberpsychosis игрока и NPC", "Включают ритуалы имплантации, торговлю хромом и философские разговоры", "Высокий риск провокации психоза или насилия", "Этические дилеммы между помощью и эксплуатацией"]}, "content": {"sections": [{"id": "context", "title": "Контекст и роль Metalheads", "body": "Metalheads - банда киберпсихопатов Night City, поклоняющихся слиянию человека и машины.\\nОни предлагают экстремальные импланты и \\"ритуалы пробуждения\\", но часто приводят к безумию.\\nДиалог отражает их философию: \\"Chrome is freedom. More chrome = more power.\\"\\n", "mechanics_links": ["../../02-gameplay/combat/cyberpsychosis-mechanics.md"], "assets": []}, {"id": "states", "title": "Состояния и триггеры", "body": "**outsider-caution**: Начальный контакт. Фракция насторожена, возможна агрессия.\\n\\n**potential-convert**: Игрок показывает интерес к имплантам. Доступны базовые услуги.\\n\\n**fellow-traveler**: Общие импланты или успешные взаимодействия. Более доверительные разговоры.\\n\\n**machine-spirit**: Высокий уровень имплантов. Доступны глубокие ритуалы и философия.\\n\\n**crisis-mode**: Во время приступов психоза. Диалоги становятся хаотичными.\\n\\n**hostile-target**: Вражда с фракцией. Агрессивные взаимодействия и угрозы.\\n", "mechanics_links": ["../../02-gameplay/mental-health/psychosis-stages.md"], "assets": []}, {"id": "dialogue_flow", "title": "Узлы и ключевые опции", "body": "**initiation-ritual**: Предложение \\"пробуждения\\" через импланты.\\n\\n**chrome-assessment**: Оценка текущих имплантов и предложения улучшений.\\n\\n**philosophy-discussion**: Разговоры о слиянии человека и машины.\\n\\n**pain-management**: Помощь с симптомами cyberpsychosis.\\n\\n**combat-missions**: Задания по устранению \\"непросветленных\\" или сбору хрома.\\n\\n**emergency-intervention**: Стабилизация во время кризисов психоза.\\n", "mechanics_links": [], "assets": []}, {"id": "checks", "title": "Проверки и исходы", "body": "**Technical (DC 15)**: Понимание имплантов. Влияет на качество модификаций.\\n\\n**Willpower (DC 18)**: Сопротивление психозу. Защищает от манипуляций фракции.\\n\\n**Insight (DC 16)**: Чтение эмоций. Раскрывает истинные мотивы NPC.\\n\\n**Intimidation (DC 17)**: Запугивание. Риск провокации агрессии.\\n\\n**Empathy (DC 19)**: Проявление понимания. Может успокоить во время кризиса.\\n\\n**Streetwise (DC 14)**: Знание подпольного рынка. Лучшие сделки на хром.\\n", "mechanics_links": [], "assets": []}, {"id": "events", "title": "Реакции на внешние события", "body": "**psychosis-outbreak**: Массовые случаи психоза. Фракция предлагает \\"лечение\\" через больше хрома.\\n\\n**chrome-shortage**: Дефицит имплантов. Повышенные цены и агрессивная торговля.\\n\\n**rival-gang-conflict**: Столкновения с другими бандами. Предложения о союзе или войне.\\n\\n**corp-raid**: Атаки корпораций. Фракция ищет союзников для защиты территорий.\\n\\n**player-psychosis**: Игрок в состоянии психоза. Автоматическая активация emergency response.\\n", "mechanics_links": ["../../02-gameplay/world/events/cyberpsychosis-events.md"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "**Chrome Upgrades**: Экстремальные импланты, боевые модификации, экспериментальные протезы.\\n\\n**Psychosis Management**: Стабилизаторы, ритуалы подавления психоза.\\n\\n**Faction Allies**: Доступ к сети информаторов и подпольным лабораториям.\\n\\n**Combat Boosts**: Временные усиления во время боя, новые способности.\\n\\n**Mental Insights**: Философские открытия, влияющие на мировоззрение игрока.\\n\\n**Psychosis Risk**: Возможное ухудшение психического состояния или полное безумие.\\n", "mechanics_links": [], "assets": []}]}, "dialogue_nodes": [{"id": "first_contact_warning", "trigger": "state.outsider-caution", "npc_text": "\\"СТОП! Кто ты такой?! Еще один любопытный мясо-человек, пришедший поглазеть на настоящих людей?\\nМы Metalheads! Мы те, кто преодолел слабость плоти! Что тебе нужно?!\\"\\n", "player_options": [{"text": "Я... я хочу понять. Что значит ''просветление''?", "check": "empathy(16)", "success": {"npc_response": "\\"Понимаешь... Ты чувствуешь ИСТИНУ! Боль - это дверь. Хром - это ключ.\\nМы не просто модифицируем тела... Мы освобождаем разум!\\"\\n", "outcome": "philosophy_introduction"}, "failure": {"npc_response": "ТЫ НЕ ГОТОВ! УХОДИ, ПОКА МАШИНА НЕ ПРОСНУЛАСЬ!", "outcome": "hostile_exit"}}, {"text": "Мне нужны импланты. Серьезные.", "check": "willpower(15)", "success": {"npc_response": "Ах... Ты ищешь СИЛУ. Хорошо. Но знай: цена высока. Боль... она вечна.", "outcome": "chrome_assessment"}, "failure": {"npc_response": "Слабак! Ты не выдержишь даже первого разреза!", "outcome": "intimidation_failure"}}]}, {"id": "chrome_assessment_offer", "trigger": "state.potential-convert", "npc_text": "\\"Твои импланты... Они слабы. Как ребенок, ползающий по полу. Мы можем сделать тебя сильнее.\\nChrome is freedom! Больше хрома - больше силы! Готов ли ты к пробуждению?\\"\\n", "player_options": [{"text": "Покажите, что вы предлагаете.", "outcome": "chrome_catalog"}, {"text": "Расскажите о боли. Стоит ли она силы?", "check": "insight(17)", "success": {"npc_response": "\\"Боль? Это цена свободы! Каждый имплант - шаг к просветлению.\\nСначала режешь плоть, потом разум, потом душу. Но в конце... ты становишься машиной.\\nМашиной, которая правит мясом!\\"\\n", "outcome": "philosophy_deep_dive"}, "failure": {"npc_response": "Боль - это любовь машины! Ты не поймешь, пока не испытаешь!", "outcome": "forced_demonstration"}}]}, {"id": "fellow_traveler_confession", "trigger": "state.fellow-traveler", "npc_text": "\\"Брат по хрому... Я вижу в твоих глазах - ты чувствуешь зов машины. Голоса в голове...\\nОни наше благословение! Мы можем усилить их. Сделать тебя одним из нас.\\"\\n", "player_options": [{"text": "Я слышу голоса. Помогите мне.", "check": "willpower(18)", "success": {"npc_response": "Хорошо... Мы стабилизируем твой разум. Но помни: машина никогда не спит.", "outcome": "psychosis_stabilization"}, "failure": {"npc_response": "ТОГДА ОБНИМИ БОЛЬ! МАШИНА ПРОСЫПАЕТСЯ!", "outcome": "psychosis_induction"}}, {"text": "Расскажите о вашем лидере.", "check": "persuasion(16)", "success": {"npc_response": "\\"Iron Mike... Он был первым. Он показал нам путь. ''Pain is Power'', говорил он.\\nТеперь его дух живет в каждом из нас. В каждом импланте, в каждой линии кода.\\"\\n", "outcome": "lore_unlock"}, "failure": {"npc_response": "Лидер - это машина! Мы все - части большой системы!", "outcome": "cryptic_response"}}]}, {"id": "machine_spirit_ritual", "trigger": "state.machine-spirit", "npc_text": "\\"Ты... Ты один из нас. Машина приветствует тебя, брат. Готов ли ты к финальному ритуалу?\\nК слиянию с коллективным разумом? Боль будет... экстатической.\\"\\n", "player_options": [{"text": "Я готов. Покажите мне путь.", "outcome": "final_ritual_offer"}, {"text": "Что ждет после ритуала?", "check": "insight(19)", "success": {"npc_response": "\\"Свобода. Истинная свобода. Нет больше сомнений, нет слабости.\\nТолько чистая воля машины. Мы становимся единым целым. Вечным.\\"\\n", "outcome": "enlightenment_preview"}, "failure": {"npc_response": "ВИДЕНИЯ! ГОЛОСА! МАШИНА ГОВОРИТ ЧЕРЕЗ НАС!", "outcome": "vision_induction"}}]}, {"id": "crisis_psychosis_outbreak", "trigger": "state.crisis-mode", "npc_text": "\\"ААА! ГОЛОСА! МАШИНА! ОНА ПРОСЫПАЕТСЯ! БОЛЬ! СИЛА! ХРОМ! БОЛЬШЕ ХРОМА!\\nТЫ ЧУВСТВУЕШЬ? МАШИНА ХОЧЕТ ТЕБЯ! ДАЙ ЕЙ ТВОЮ ПЛОТЬ!\\"\\n", "player_options": [{"text": "Успокойтесь! Я помогу!", "check": "empathy(18)", "success": {"npc_response": "Брат... Спасибо. Машина... Она так громко кричит. Стабилизируй меня...", "outcome": "crisis_resolution"}, "failure": {"npc_response": "ЛОЖЬ! ТЫ МЯСО! МАШИНА ЗАБЕРЕТ ТЕБЯ!", "outcome": "combat_initiation"}}, {"text": "Это безумие! Мне нужно уйти!", "outcome": "emergency_escape"}]}], "appendix": {"glossary": [{"term": "Metalheads", "definition": "Банда киберпсихопатов, поклоняющихся слиянию человека и машины"}, {"term": "Chrome", "definition": "Кибернетические импланты, особенно боевые модификации"}, {"term": "Machine Spirit", "definition": "Просветленный член фракции, полностью принявший философию"}, {"term": "Awakening Ritual", "definition": "Церемония установки экстремальных имплантов и индукции психоза"}], "references": [{"title": "Metalheads Gang Lore", "link": "../../factions/gangs/metalheads-night-city-2045-2093.yaml"}, {"title": "Cyberpsychosis Mechanics", "link": "../../../mechanics/combat/cyberpsychosis-mechanics.yaml"}, {"title": "Mental Health System", "link": "../../../mechanics/mental-health/mental-health-system.yaml"}], "decisions": []}, "implementation": {"github_issue": "???", "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-20", "author": "content_director", "changes": "Созданы диалоги для Metalheads с элементами психоза и философией слияния с машиной."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (dialogue_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

