INSERT INTO narrative.dialogue_nodes (dialogue_id, title, content_data, version, is_active) VALUES ('dialogue-npc-hiroshi-tanaka', 'Диалог Хироши Танака', '{"metadata": {"id": "dialogue-npc-hiroshi-tanaka", "title": "Диалог Хироши Танака", "document_type": "canon", "category": "dialogue", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "arasaka", "dialogue"], "topics": ["corporate-recruitment", "branching-dialogues"], "related_systems": ["narrative-service", "corporate-reputation-service"], "related_documents": [{"id": "npc-lore-hiroshi-tanaka", "relation": "references"}, {"id": "quest-arasaka-serenity", "relation": "produces"}], "source": "shared/docs/knowledge/canon/narrative/dialogues/npc-hiroshi-tanaka.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Диалог корпоративного рекрутёра Arasaka существовал в Markdown и не был оформлен в соответствии со схемой знаний.", "goal": "Формализовать состояния, проверки и события Хироши Танаки для корпоративных сценариев Arasaka.", "essence": "Танака проверяет кандидатов, выдаёт операции уровня А и жестко реагирует на связь с Militech и режим Lockdown.", "key_points": ["Состояния base, loyal, suspicious и lockdown управляются репутацией Arasaka и глобальными событиями.", "Узлы assessment, loyalty-briefing, suspicion и lockdown-directive покрывают проверку кандидата, выдачу контрактов и кризисные протоколы.", "Репутационные последствия затрагивают Arasaka, Militech и активируют контракты вроде `arasaka-serenity` и эвент `arasaka-lockdown-response`."]}, "content": {"sections": [{"id": "context", "title": "Контекст", "body": "Хироши Танака — агент Arasaka по набору оператов. Диалог определяет доступ к корпоративным операциям,\\nуправляет clearance и отвечает на корпоративные кризисы.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "states", "title": "Состояния и флаги", "body": "Репутация Arasaka и флаг `flag.arasaka.clearanceA` переводят игрока в состояние loyal, а контакты с\\nMilitech вызывают suspicious. Событие `world.event.arasaka_lockdown` активирует протокол Kōsetsu.\\n", "mechanics_links": ["shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka/ceo-arasaka-2020-2030.yaml"], "assets": []}, {"id": "nodes", "title": "Узлы и проверки", "body": "Проверки Persuasion, Strategy, Deception и Composure определяют выдачу clearance, контрактов и переходы\\nмежду состояниями. Узлы assessment, loyalty-briefing, suspicion и lockdown-directive обрабатывают ключевые\\nсценарии взаимодействия.\\n", "mechanics_links": ["mechanics/narrative/quest-system.yaml"], "assets": []}, {"id": "integrations", "title": "Интеграции и инструменты", "body": "Диалог экспортируется в narrative-service, поддерживается REST/GraphQL методами и симулируется скриптами\\n`validate-dialogue-flags.ps1` и `dialogue-simulator.ps1`. Телеметрия отслеживает метрики по успешным\\nпроверкам и срабатыванию протокола Lockdown.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Награды и последствия", "body": "Репутация Arasaka изменяется в диапазоне ±15, выдаются предметы вроде `arasaka-delta-suit`, активируются\\nконтракты `arasaka-serenity` и эвент `arasaka-lockdown-response`, а также флаги blacklist и silence_note.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 107, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован диалог Хироши Танаки в формат знаний с выделенными состояниями и протоколами Lockdown."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (dialogue_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

