INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-valentinos-underboss-2065-2080', 'Underboss Valentinos (2065-2080)', '{"metadata": {"id": "npc-valentinos-underboss-2065-2080", "title": "Underboss Valentinos (2065-2080)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "valentinos", "diplomacy"], "topics": ["neighborhood-politics", "organized-crime"], "related_systems": ["narrative-service", "social-system"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/valentinos/underboss-2065-2080.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль underboss Valentinos оставался в Markdown и не применялся в сюжетах дипломатии Heywood.", "goal": "Структурировать образ Диего «Санто» Риверы для квестов перемирий, сделок и трансформации бизнеса.", "essence": "Карает и защищает Heywood, удерживая баланс между честью кварталов и прибыльными схемами.", "key_points": ["Управляет переговорами Valentinos с 6th Street, Tyger Claws и корпорациями.", "Поддерживает кодекс чести и стремится легализовать доходы.", "Нуждается в союзниках для предотвращения внутренних предательств."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Диего «Санто» Ривера — правая рука Jefe Valentinos в Heywood. Сочетает религиозные символы и уличный стиль, носит\\nимпланты Kiroshi и Smart Link для защиты и переговоров.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "operations", "title": "Роль в клане", "body": "Курирует «мир» между кварталами, организует сделки с корпорациями и следит за соблюдением кодекса Valentinos.\\nВедёт бизнес так, чтобы семья получала стабильный доход без хаоса.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Удерживает честь и семью, стремится стать легальным бизнесменом. Боится предательства изнутри и потери влияния\\nв Heywood.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт дипломатические квесты, сделки и миссии по удержанию перемирий. Навыки: социальные — высокие, боевые —\\nсредние, технические — низкие.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Заключение перемирия с 6th Street против Tyger Claws.\\n- Выявление предателя, работающего на корпорации.\\n- Попытка легализовать один из бизнесов Valentinos.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль underboss Valentinos в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

