INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-wraiths-mechanic-2055-2085', 'Мастер-механик Wraiths (2055-2085)', '{"metadata": {"id": "npc-wraiths-mechanic-2055-2085", "title": "Мастер-механик Wraiths (2055-2085)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "wraiths", "mechanics"], "topics": ["nomad-engineering", "convoy-maintenance"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/wraiths/master-mechanic-2055-2085.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль главного механика Wraiths находился в Markdown и не использовался в сюжетах логистики пустошей.", "goal": "Формализовать образ Рози «Железной Розы» Кастильо для квестов ремонта, обучения и модернизации техники клана.", "essence": "Практичная наставница поддерживает флот Wraiths в боеспособном состоянии, превращая хлам пустошей в боевые машины.", "key_points": ["Руководит мобильной мастерской и обучает новое поколение механиков.", "Создала легендарный «Железный Дракон» — таранящий грузовик.", "Предлагает игроку технические квесты и уникальные модификации."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Рози «Железная Роза» Кастильо — главный механик каравана Wraiths. Её передвижная мастерская собрана из цистерны\\nи набита инструментами, запчастями и незавершёнными проектами. Она строгая, но заботливая наставница.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "operations", "title": "Роль в клане", "body": "Обслуживает флот грузовиков и бронемашин, управляет запасами топлива и деталей, модернизирует трофейное оружие.\\nБез её работы караваны останавливаются, а рейды становятся невозможными.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится передать знание молодым механикам и создать парк машин, способный пережить любые условия пустошей.\\nБоится, что без подготовки следующего поколения клан останется без транспорта.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт квесты «Воскрешение железа» и «Ученик мастера», предоставляя игроку доступ к уникальным модификациям и\\nобучению пустошной механике. Навыки: технические — экстремальные, социальные — средние, боевые — низкие.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Поиск редких деталей на заброшенной базе Militech.\\n- Защита мастерской от дронов и рейдеров конкурентов.\\n- Выбор преемника среди молодых рейдеров.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль мастера-механика Wraiths в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

