INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-voodoo-net-architect-2063-2078', 'Архитектор ритуалов NET — Voodoo Boys (2063-2078)', '{"metadata": {"id": "npc-voodoo-net-architect-2063-2078", "title": "Архитектор ритуалов NET — Voodoo Boys (2063-2078)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "voodoo-boys", "pacifica"], "topics": ["net-rituals", "community-protection"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/voodoo-boys/net-ritual-architect-2063-2078.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание архитектора ритуалов NET находилось в Markdown и не использовалось в сюжетах погружений Voodoo Boys.", "goal": "Стандартизировать данные о Тибо «Лоа» Дювале для ритуальных квестов и защиты общины Pacifica.", "essence": "Инженер ритуалов превращает духовные практики Voodoo Boys в безопасные протоколы глубокого погружения и обучает общину.", "key_points": ["Стандартизировал ритуалы, снизив смертность нетраннеров при погружениях.", "Комбинирует верования и код, создавая защищённые святилища NET.", "Предоставляет игрокам доступ к уникальным бустерам и миссиям Voodoo Boys."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Тибо «Лоа» Дюваль — архитектор сетевых ритуалов Voodoo Boys. Он проектирует церемонии, соединяющие духовную\\nпрактику и строгие протоколы безопасности, чтобы община могла работать за Blackwall.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "achievements", "title": "История и достижения", "body": "Стандартизировал глубинные протоколы и обучил операторов, что сократило смертность нетраннеров. Создал систему\\nкультурных ключей, позволяющих безопасно делиться знаниями внутри общины Pacifica.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивации и цели", "body": "Стремится защитить сакральность ритуалов и обеспечить автономию Voodoo Boys. Боится похищения протоколов и\\nпрофанации традиций корпорациями и NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи", "body": "- Maman Brigitte — доверие и совместная работа над ритуалами.\\n- NetWatch — постоянное противостояние из-за ритуальных святилищ.\\n- Mr. Hands — прагматичные сделки по ресурсам и укрытиям.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Запускает ритуальные цепочки, выдаёт доступ к защитным бустерам и специальным модификаторам для нетраннинга.\\nНавыки: нетраннинг — очень высокие, социальные — средние, боевые — низкие.\\n", "mechanics_links": ["mechanics/combat/combat-hacking-combat-integration.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Проведение коллективного ритуала для защиты узла Pacifica.\\n- Охота NetWatch за ритуальными ключами и выбор: скрыть или поделиться.\\n- Постройка нового святилища NET с участием игрока.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль архитектора ритуалов NET в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

