INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-ncpd-captain-2065-2085', 'Капитан-детектив NCPD (2065-2085)', '{"metadata": {"id": "npc-ncpd-captain-2065-2085", "title": "Капитан-детектив NCPD (2065-2085)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "ncpd", "detective"], "topics": ["corruption", "major-crimes"], "related_systems": ["narrative-service", "social-system"], "related_documents": [{"id": "scenarios-factions-law-ncpd-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/ncpd/captain-detective-2065-2085.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль честного капитана NCPD хранился в Markdown и не подключался к сюжетам внутренней коррупции полиции.", "goal": "Формализовать данные о Дженне Рейес для расследовательских квестов и конфликтов с корпорациями.", "essence": "Циничная идеалистка продолжает служить в гнилой системе, чтобы спасать тех, кого ещё можно спасти.", "key_points": ["Известна как «Чистая Копка», не берёт взятки и работает против корпораций.", "Раскрыла связь руководства NCPD с Tyger Claws и пережила покушение.", "Использует игрока для дел, которые система пытается замять."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Дженна Рейес возглавляет отдел тяжких преступлений в Watson. Её офис завален доказательствами по делам, которые\\nникто не хочет расследовать, и списком людей, которых нужно защитить от собственной полиции.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "history", "title": "История и события", "body": "Выросла в Watson, вступила в NCPD ради справедливости. В 2075 году разоблачила коррупцию в руководстве, едва\\nвыжила, но не отступила. Понимает, что систему не исправить, но отдельные жизни спасать можно.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и позиция", "body": "Продолжает служить, чтобы честные люди не остались без защиты. Цинично относится к системе, но не сдаётся,\\nсчитая, что её уход оставит улицы полностью бандам и корпорациям.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Выдаёт квесты «Чистые руки в грязной системе» и «Последнее дело», в которых игрок действует вне закона, пока\\nДженна обеспечивает прикрытие. Навыки: аналитические и социальные — высокие, боевые — средние.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Расследование убийства, скрываемого корпорацией.\\n- Сбор доказательств против коррумпированных офицеров.\\n- Решение Дженны уйти или остаться после последнего дела.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль капитана-детектива NCPD в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

