-- NPC: npc-maxtac-commander-atlas-2070-2090
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-maxtac-commander-atlas-2070-2090', 'Commander "Atlas" MAX-TAC (2070-2090)', '{"metadata": {"id": "npc-maxtac-commander-atlas-2070-2090", "title": "Commander \\"Atlas\\" MAX-TAC (2070-2090)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "maxtac", "commander"], "topics": ["cyberpsychosis", "elite-ops"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-law-ncpd-maxtac-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/ncpd/maxtac/commander-atlas-2070-2090.md", "visibility": "internal", "audience": ["concept", "narrative", "combat"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль командира MAX-TAC существовал в Markdown и не использовался в сюжетах о киберпсихозе и элитных операциях.", "goal": "Структурировать образ Майи «Atlas» Кросс для миссий искупления и управления отрядом MAX-TAC.", "essence": "Бывший киберпсихопат стала командиром, ведя команду монстров, которые охотятся на монстров, и сражается со своим прошлым.", "key_points": ["Пережила собственный киберпсихоз и перешла на службу MAX-TAC.", "Командует самым эффективным отрядом, сохраняя минимальные потери среди гражданских.", "Борется с постоянным страхом сорваться вновь и ищет искупление."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Майя «Atlas» Кросс руководит отрядом MAX-TAC из City Center, лично участвуя в операциях. Её тело на 75% состоит\\nиз кибернетики, а взгляд отражает дисциплину и холодный контроль, необходимый для охоты на киберпсихов.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "origins", "title": "История и падение", "body": "В прошлом честный офицер NCPD, в 2068 году она сорвалась и стала киберпсихопатом, убив семерых. MAX-TAC\\nнейтрализовал её и предложил выбор — смерть или служба. Atlas выбрала службу для искупления, прошла жёсткую\\nтерапию и вернулась как оператор.\\n", "mechanics_links": [], "assets": []}, {"id": "command", "title": "Командование и достижения", "body": "С 2070 по 2090 годы Atlas возглавляет элитный отряд из модифицированных бойцов. Её команда проводит 98%\\nопераций успешно, а опыт Atlas помогает предугадывать действия киберпсихов, поскольку она понимает их мышление.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и страхи", "body": "Стремится искупить убийства, спасая больше жизней, чем забрала. Боится повторного психоза, поэтому носит\\nимплант Killswitch и проходит регулярные психоиспытания. Каждая операция — борьба за своё человечество.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает игроку квесты «Охота на себя», «Killswitch» и «Последняя операция», раскрывающие внутренний конфликт\\nMAX-TAC. Навыки: боевые и лидерские — экстремальные, социальные — средние, человечность — на грани.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Расследование саботажа Killswitch Protocol.\\n- Миссия по спасению киберпсихопата, повторяющего путь Atlas.\\n- Дилемма между жертвой ради команды и сохранением контроля.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль командира MAX-TAC Atlas в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

