INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-evelyn-parker', 'Эвелин Паркер — куратор braindance-афёр', '{"metadata": {"id": "npc-evelyn-parker", "title": "Эвелин Паркер — куратор braindance-афёр", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "braindance", "fixer"], "topics": ["npc-biographies", "heist-design"], "related_systems": ["narrative-service", "stealth-service"], "related_documents": [{"id": "canon-narrative/stories/cyberpunk-inspiration-stories", "relation": "references"}, {"id": "canon-narrative/dialogues/npc-judy-alvarez", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/evelyn-parker.md", "visibility": "internal", "audience": ["concept", "narrative", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание Эвелин Паркер было в Markdown и не связывалось с квестами Night City и braindance-контентом.", "goal": "Структурировать данные о персонаже для афёр, сюжетов Westbrook и сетевых связей.", "essence": "Гламурная и расчётливая куратор braindance, добывающая информацию ради свободы и власти.", "key_points": ["Работает через Lizzie''s Bar и подпольные студии Westbrook.", "Инициирует сложные афёры против корпораций, используя BD-технологии.", "Поддерживает тесные связи с Джуди Альварез и опасные сделки с фиксёрами.", "Награждает игроков доступом к эксклюзивным записям и инсайдам."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Эвелин Паркер (`npc-evelyn-parker`) — независимый куратор braindance и связной для высокорисковых операций.\\nРаботает в ночных клубах Westbrook, включая Lizzie''s Bar. Стиль — гламур-неон, холодная уверенность и скрытые мотивы.\\n", "mechanics_links": [], "assets": []}, {"id": "expertise", "title": "Экспертиза и методы", "body": "Имеет сеть контактов среди техников BD и фиксёров. Организует инфильтрации, сбор компромата и многоходовые афёры,\\nиспользуя доступ к эксклюзивным записям.\\n", "mechanics_links": ["mechanics/combat/combat-hacking-networks.yaml"], "assets": []}, {"id": "motivations", "title": "Мотивация и цели", "body": "Стремится к свободе и контролю через информацию. Всегда действует на опережение, выбирая союзников исходя из пользы.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения", "body": "Сохраняет профессиональные и личные связи с Джуди Альварез. Ведёт рискованные сделки с корпорациями, что создаёт\\nнапряжённые сюжетные ветки.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Игровое применение", "body": "Выдаёт квесты на подготовку афёр, проникновение в защищённые клубы и извлечение данных. Игроки получают доступ\\nк закрытым BD и инсайдам о корпоративных целях.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "dialogue", "title": "Диалоговые акценты", "body": "Ключевые цитаты: «Правильная запись — билет в любой кабинет.»; «Каждый носит маску. Некоторые — по три.»\\nТон общений — провокационно-холодный, она тестирует намерения игрока.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 28, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-evelyn-parker"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертирован профиль Эвелин Паркер в формат YAML и связан с сюжетами braindance."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

