INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-nomad-snake-elder-2040-2080', 'Старейшина Snake Nation (2040-2080)', '{"metadata": {"id": "npc-nomad-snake-elder-2040-2080", "title": "Старейшина Snake Nation (2040-2080)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "snake-nation", "nomads"], "topics": ["nomad-culture", "spiritual-leadership"], "related_systems": ["narrative-service", "worldbuilding-service"], "related_documents": [{"id": "scenarios-factions-nomads-snake-nation-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/nomads/snake-nation/elder-snake-2040-2080.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль духовного лидера Snake Nation существовал только в Markdown и не был согласован с каноном.", "goal": "Зафиксировать ключевые сведения о старейшине «Гремучая Змея» для интеграции в повествование о кочевых кланах.", "essence": "Харизматичный хранитель традиций объединяет Snake Nation через ритуалы и уважение к земле, передавая духовное наследие.", "key_points": ["Руководил кланом в течение сорока лет и удерживал союз Nomad-кланов через ритуалы.", "Признаёт технологии как инструмент, но ставит дух и традиции выше материальных выгод.", "Обеспечил преемственность знаний, подготовив наследника перед смертью в 2080 году."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Старейшина «Гремучая Змея» руководил Snake Nation с 2040 года. Он отвечал за духовное единство\\nкаравана, хранение священных земель и поддержание связи с предками. Его авторитет распространялся\\nдаже на кланы, враждебные Snake Nation.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и ключевые события", "body": "Клан Snake Nation вырос из традиций коренных народов Северной Америки, сохранив коллективную память\\nо земле и ритуалах. В 2075 году старейшина провёл ритуал «Возвращения духов», который собрал Nomad\\nсо всего континента и укрепил союз. В 2080 году он мирно скончался, передав знания новому лидеру.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "personality", "title": "Личность и влияние", "body": "Особенности характера: мудрость, спокойствие, готовность к компромиссу. Даже противники уважали его,\\nблагодаря чему Snake Nation избегал крупных конфликтов. Он обучал следующую генерацию лидеров\\nуважать баланс между технологией и духовностью.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Ритуал «Наследие Змеи», где игрок защищает священные земли от корпораций.\\n- Посмертное расследование тайного завещания старейшины, распределяющего маршруты караванов.\\n- Дипломатическая миссия по примирению Snake Nation с агрессивным кланом Jodas.\\n", "mechanics_links": [], "assets": []}, {"id": "quotes", "title": "Цитаты", "body": "> «Земля помнит нас. Даже когда мы забываем её.»\\n\\n> «Технологии приходят и уходят. Но дух народа вечен.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль старейшины Snake Nation в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

