INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-aldecaldos-elder-2050-2090', 'Старейшина Aldecaldos (2050-2090)', '{"metadata": {"id": "npc-aldecaldos-elder-2050-2090", "title": "Старейшина Aldecaldos (2050-2090)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "aldecaldos", "elders"], "topics": ["nomad-governance", "family-legacy"], "related_systems": ["narrative-service", "community-service"], "related_documents": [{"id": "scenarios-factions-nomads-aldecaldos-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/nomads/aldecaldos/elder-2050-2090.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Досье Марии «Мама Мария» Альдекальдо существовало только в Markdown и не описывало влияние на стратегию клана.", "goal": "Структурировать данные о старейшине Aldecaldos для использования в сюжетах о преемственности и клановой политике.", "essence": "Мудрая матриарх клана удерживает семью вместе, передаёт традиции и благословляет ключевые союзы.", "key_points": ["Одна из основателей Aldecaldos, пережившая Четвёртую корпоративную войну и коллапс США.", "Спасла караван от ядерного взрыва в 2023 году и поддержала операцию Панам и V в 2077 году.", "Организует ритуалы и благословения, обеспечивая эмоциональную опору клана."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Мария «Мама Мария» Альдекальдо — хранительница традиций Aldecaldos и моральный авторитет клана. Она\\nживёт в мобильном автобусе-доме и принимает решения, влияющие на всю семью.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и наследие", "body": "Пережив ключевые катастрофы XXII века, Мария сформировала принципы выживания и солидарности Aldecaldos.\\nВ 2023 году предчувствовала катастрофу в Night City и эвакуировала караван. В 2077 году благословила\\nПанам на союз с V против Arasaka, закрепив влияние клана в городе.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "personality", "title": "Характер и ценности", "body": "Она терпелива, заботлива и видит людей насквозь. Говорит тихо, но её мнение определяет стратегию клана.\\nГлавная цель Марии — сохранить единство и передать традиции следующему поколению.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Квест «Последнее путешествие»: сопровождение Марии к истокам Aldecaldos.\\n- Линия «Благословение старейшины»: получение артефакта, усиливающего репутацию игрока среди Nomad.\\n- Драма преемственности, где игрок помогает выбрать нового духовного лидера.\\n", "mechanics_links": [], "assets": []}, {"id": "quotes", "title": "Цитаты", "body": "> «Дороги — это наша жизнь. Мы не бежим от мира. Мы несём мир с собой.»\\n\\n> «Корпорации приходят и уходят. Но семья вечна.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль старейшины Aldecaldos в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

