-- NPC: npc-nomad-jodas-chief-2055-2085
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-nomad-jodas-chief-2055-2085', 'Вождь клана Jodas (2055-2085)', '{"metadata": {"id": "npc-nomad-jodas-chief-2055-2085", "title": "Вождь клана Jodas (2055-2085)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "jodas", "nomads"], "topics": ["raider-warfare", "nomad-politics"], "related_systems": ["narrative-service", "conflict-resolution-service"], "related_documents": [{"id": "scenarios-factions-nomads-jodas-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/nomads/jodas/chief-jodas-2055-2085.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Досье на Кейна Джодаса находилось в Markdown и не учитывало текущие конфликты Nomad-кланов.", "goal": "Описать боевой профиль лидера Jodas для сюжетов о рейдах, переговорах и угрозе биологического оружия.", "essence": "Жестокий «Железный Волк» превратил Jodas в военную машину и готов использовать любые средства ради силы.", "key_points": ["Захватил экспериментальное биооружие Biotechnica и применил его против Raffen Shiv.", "Держит клан в режиме постоянной войны, набирая бойцов через культуру силы и страха.", "Предлагает игроку выбор между уничтожением Jodas или опасным союзом."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Кейн «Железный Волк» Джодас возглавляет клан Jodas с 2055 года. Он позиционирует себя как полевого\\nкомандующего и строит клан на принципах подавления соперников, рейдерства и трофеев.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и ключевые операции", "body": "Кейн вырос в пустошах Юго-Запада и с ранних лет участвовал в подпольных боях. В 2070 году организовал\\nнападение на конвой Biotechnica и завладел биооружием, уничтожив лагерь Raffen Shiv. С тех пор Jodas\\nсчитается одной из самых опасных Nomad-группировок.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "personality", "title": "Характер и методы", "body": "Жесток, параноидален и ревностно охраняет власть. Для него сила — единственный аргумент. Лояльность\\nполучает через страх и награды, а конкурентов устраняет личным примером.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Квест «Кровь и сталь»: выбор между войной с Jodas или заключением прагматичного союза.\\n- История о поиске и нейтрализации биологического оружия, удерживаемого кланом.\\n- Дипломатическая миссия от Aldecaldos по предотвращению новой войны Nomad.\\n", "mechanics_links": [], "assets": []}, {"id": "quotes", "title": "Цитаты", "body": "> «Aldecaldos играют в семью. Мы играем в выживание. И мы побеждаем.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль лидера клана Jodas в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

