INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-fixer-dakota-smith-2030-2090', 'Dakota Smith — фиксер Badlands', '{"metadata": {"id": "canon-npc-fixer-dakota-smith-2030-2090", "title": "Dakota Smith — фиксер Badlands", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:20:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "fixer", "important"], "topics": ["narrative", "factions"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-npc-fixer-mr-hands-2040-2090", "relation": "complements"}, {"id": "canon-scenarios-factions-nomads-aldecaldos-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/fixers/dakota-smith-detailed-2030-2090.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Narrative NPC Cell", "reviewed_at": "2025-11-06T11:20:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Детализированный профиль Dakota Smith находился в Markdown и не соответствовал стандарту знаний NPC.", "goal": "Сохранить историю, личность и квестовые ветки легендарного фикcера Badlands в структурированном формате.", "essence": "Dakota — материнская фигура пустошей, защищающая свободу кочевников и поддерживающая сети Aldecaldos.", "key_points": ["Родилась в 1960, пережила корпоративные войны и созданную NUSA.", "Управляет заправкой Rocky Ridge и служит нейтральным хабом для пустошей.", "Квесты «Дороги матери» и «Последняя заправка» вовлекают игроков в конфликты корпораций и беженцев."]}, "content": {"sections": [{"id": "history", "title": "История", "body": "Dakota родилась в кочевой семье, стала свидетелем распада США и в 2030 закрепилась на Rocky Ridge. Во время событий 2077\\nпомогала V и Aldecaldos, что закрепило её статус легенды пустошей.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Суровая и прямолинейная снаружи, Dakota проявляет заботу о молодых наёмниках. Она ценит честность, ненавидит корпоративный\\nконтроль и защищает автономию Badlands.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квесты", "body": "«Дороги матери» требует сопровождения каравана с беженцами, а «Последняя заправка» противопоставляет Dakota корпорации,\\nжелающей захватить её землю.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"Пустыня не прощает слабых. Но она принимает сильных.\\" — ключевая реплика, отражающая философию Dakota и её восприятие\\nпустошей.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Профиль Mr. Hands", "link": "mr-hands-detailed-2040-2090.yaml"}, {"title": "Номады Aldecaldos", "link": "../../../../scenarios/factions/nomads/aldecaldos-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "npc_team", "changes": "Создан детализированный профиль Dakota Smith."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

