INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-jackie-welles', 'Джеки Уэллс — напарник улиц Night City', '{"metadata": {"id": "npc-jackie-welles", "title": "Джеки Уэллс — напарник улиц Night City", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-02T18:50:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "merc", "valentinos"], "topics": ["npc-biographies", "starter-companions"], "related_systems": ["social-service", "gameplay-service", "narrative-service", "character-service", "quest-service"], "related_documents": [{"id": "canon-narrative/stories/cyberpunk-inspiration-stories", "relation": "references"}, {"id": "mechanics/world/events/live-events-system", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/jackie-welles.md", "visibility": "internal", "audience": ["concept", "narrative", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Джеки существовал в Markdown и не был связан с системами наставничества и сюжетами Valentinos.", "goal": "Сформировать структурированное описание напарника для стартовой кампании Street Kid и событий Heywood.", "essence": "Харизматичный наёмник с прошлым в Valentinos, помогая игрокам влиться в Night City и дать стартовую опору.", "key_points": ["Выходец из Heywood, бывший Valentinos, теперь независимый наёмник и наставник новичков.", "Строит цепочки дружбы, славы и кооперативных миссий, открывая доступ к фиксерам.", "Балансирует между семейными ценностями и стремлением к славе, поддерживая связь с Misty.", "Используется в Live Ops как лицо уличных событий и социального продвижения."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и роль", "body": "Джеки Уэллс (ID `npc-jackie-welles`) — независимый наёмник, вышедший из Valentinos. Работает в Heywood, но легко\\nперемещается по городу. Высокий, атлетичный, с фирменной улыбкой и татуировками Valentinos. Для старта Street Kid\\nвыступает в роли напарника и связующего звена с городом.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и мотивация", "body": "Вырос среди Valentinos, но ушёл, чтобы добиться славы и богатства. Поддерживает отношения с бандой и фиксерами,\\nценит семью (Мисты, мать). Боится потерять друзей и умереть бесславно. Живёт мечтой о легендарном статусе и\\nготов передать опыт новичкам.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "relationships", "title": "Связи и влияние", "body": "Дружит с Марко «Фиксом» Санчесом, поддерживает Misty и старых товарищей из Valentinos. Отношения с корпорациями\\nпрагматичные: работает, если платят. С игроками быстро устанавливает доверие и расширяет список контактов.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровые функции", "body": "Выдаёт стартовые цепочки «Первые шаги», «Напарник», «Путь к славе», проводит по городским системам. Обеспечивает\\nдоступ к фиксерам, обучает кооперативным действиям, помогает в уличных ивентах. Награды — ресурсы, репутация и\\nсоциализация с Valentinos.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "dialogue", "title": "Диалоговые акценты", "body": "Ключевые реплики: «Чомбата, давай работать вместе!», «Мы команда, мы справимся!», «К славе и богатству!».\\nТон — дружелюбный и мотивирующий. При низкой репутации становится сдержанным, но не переходит в открытую вражду.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 16, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-jackie-welles"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован профиль Джеки Уэллса и связан с системами наставничества Street Kid."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

