-- NPC: canon-npc-fixer-mr-hands-2040-2090
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-fixer-mr-hands-2040-2090', 'Mr. Hands — главный фиксер Pacifica', '{"metadata": {"id": "canon-npc-fixer-mr-hands-2040-2090", "title": "Mr. Hands — главный фиксер Pacifica", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:15:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "fixer", "pacifica"], "topics": ["narrative", "factions"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-npc-fixer-dakota-smith-2030-2090", "relation": "complements"}, {"id": "canon-scenarios-locations-night-city-pacifica-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/fixers/mr-hands-detailed-2040-2090.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Narrative NPC Cell", "reviewed_at": "2025-11-06T11:15:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Максимально детализированный профиль Mr. Hands находился только в Markdown и был недоступен в структурированной базе знаний.", "goal": "Закрепить историю, мотивацию и квестовые линии ключевого фикcера Pacifica для дальнейшей работы сценаристов и дизайнеров.", "essence": "Mr. Hands — нейтральный архитектор доверия, управляющий Pacifica через сеть из 10 000 контактов и баланс интересов корпораций, банд и кочевников.", "key_points": ["С 2040 года поддерживает Pacifica как свободную зону за счёт посредничества и торговли информацией.", "У него есть уникальный набор имплантов для управления контактами и сетями.", "В поздней эпохе ищет преемника и борется с усталостью, защищая баланс района."]}, "content": {"sections": [{"id": "profile", "title": "Основная информация", "body": "Муамар \\"Mr. Hands\\" Рейес — 78-летний фиксер, контролирующий Pacifica. Он редко покидает Grand Imperial Mall, предпочитая\\nуправлять связями дистанционно через агентские сети, импланты Kiroshi и расширенный нейролинк.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История", "body": "Родившийся на Гаити в 1970 году, Mr. Hands пережил корпоративные войны и мигрировал в Night City около 2000 года.\\nОн остался в Pacifica после корпоративного коллапса, превратив заброшенный район в сеть сделок. К 2060-м годам стал неофициальным\\nправителем, которого уважают и Voodoo Boys, и корпорации. После событий 2077 продолжает удерживать баланс, несмотря на возраст.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Его философия — нейтралитет и баланс. Долгосрочно он хочет сохранить Pacifica вне корпоративного контроля и найти преемника.\\nСтрахи: хаос после его смерти и потеря базы данных контактов. В краткосрочной перспективе он гасит конфликты NetWatch и Voodoo Boys.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и манеры", "body": "Спокойный, рассудительный и патерналистский. Обладает феноменальной памятью, говорит медленно, редко проявляет эмоции.\\nСкрывает усталость за безупречным стилем и манерами корпоративного топ-менеджера 1990-х.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи", "body": "Поддерживает рабочие отношения с Voodoo Boys, NetWatch, крупными корпорациями и кочевниками. Уважает коллег-фиксеров вроде Dakota,\\nно доверяет немногим. Любой наёмник оценивается по надёжности и выполненным контрактам.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квестовые линии", "body": "«Тысяча контактов» развивает репутацию игрока в Pacifica через серию поручений. «Последний фиксер» исследует попытки конкурента захватить\\nбизнес. «Баланс Pacifica» заставляет игрока предотвращать зачистку Voodoo Boys силами NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Развитие персонажа", "body": "Возможны три финальные дуги: передача бизнеса преемнику, гибель и хаос в Pacifica или организация крупнейшего мирного соглашения,\\nпосле которого Mr. Hands уходит.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Dakota Smith — профиль", "link": "dakota-smith-detailed-2030-2090.yaml"}, {"title": "Pacifica 2020-2093", "link": "../../../../scenarios/locations/night-city/pacifica-2020-2093.md"}, {"title": "Сети фиксера", "link": "../../../../scenarios/social/fixers-network-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "npc_team", "changes": "Создан детализированный профиль Mr. Hands."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

