INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-james-iron-reed', 'Джеймс «Железный» Рид — военный агент Militech', '{"metadata": {"id": "npc-james-iron-reed", "title": "Джеймс «Железный» Рид — военный агент Militech", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "militech", "military"], "topics": ["npc-biographies", "corporate-conflict"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "canon-narrative/scenarios/factions/corporations/militech-2020-2093", "relation": "complements"}, {"id": "mechanics/world/events/world-events-framework", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/james-iron-reed.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание военного агента Militech хранилось в Markdown и не связывалось с корпоративными войнами и системами лояльности.", "goal": "Структурировать ключевые данные о Джеймсе Риде для квестов Militech и PvP/PvE конфликтов с Arasaka.", "essence": "Жёсткий агент Militech, отвечающий за набор, операции и поддержание влияния корпорации в Night City.", "key_points": ["Ветеран 40–45 лет с полным набором военных имплантов и строгим корпоративным кодексом.", "Управляет программами набора и лояльности Militech в Downtown, координирует наёмников.", "Формирует сюжетные ветки корпоративных войн и проверяет игроков на верность.", "Предоставляет доступ к военным ресурсам, операциям и репутации Militech."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и роль", "body": "Джеймс «Железный» Рид (ID `npc-james-iron-reed`) — военный агент Militech, базирующийся в штаб-квартире Downtown.\\nВысокий, мускулистый, с темными волосами с проседью и шрамами боевых операций. Стиль — строгая форма Militech без\\nлишних украшений, подчёркивающая дисциплину и статус.\\n", "mechanics_links": [], "assets": []}, {"id": "augmentations", "title": "Импланты и снаряжение", "body": "Оснащён высокоуровневыми имплантами связи, боевыми модулями и усилителями выносливости. Использует стандартное\\nвооружение Militech и лёгкую тактическую броню. В распоряжении — защищённые каналы связи и доступ к корпоративным\\nресурсам.\\n", "mechanics_links": ["mechanics/combat/combat-implants-mechanics.yaml"], "assets": []}, {"id": "biography", "title": "Биография и служба", "body": "Вырос в США, рано вступил в Militech и прошёл полный курс военной подготовки. Карьера строилась на участии в\\nоперациях корпорации, наборе персонала и работе с наёмниками. В MMO Рид — главный контакт корпоративной арки Militech,\\nвыдающий задания на укрепление влияния корпорации.\\n", "mechanics_links": [], "assets": []}, {"id": "motivations", "title": "Мотивация и характер", "body": "Стремится расширять влияние Militech и защищать корпоративные интересы. Боится утратить позиции и проиграть\\nконкурентам. Характер холерический, речь формально-военная; ключевые фразы подчёркивают долг и безопасность.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения и влияние", "body": "Поддерживает дружелюбие с Militech, враждует с Arasaka и криминальными бандами. Взаимодействует с NCPD по необходимости.\\nС игроками строит отношения через систему репутации: верность вознаграждается привилегиями, сотрудничество с\\nконкурентами ведёт к конфликту.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Игровое применение", "body": "Выдаёт цепочки «Выбор корпорации», «Военные задания», «Лояльность» и «Военные операции». Доступные награды —\\nвоенное снаряжение, информация и уникальные контракты. Боевые навыки максимальны, социальные — профессиональные,\\nхакерские — на уровне базовой подготовки.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "dialogue", "title": "Диалоговые акценты", "body": "Реплики подчёркивают дисциплину: «Militech ценит силу и безопасность.», «Работа для корпорации — это долг.»,\\n«Ты работаешь с конкурентами? Тогда мы не друзья.» Он держит дистанцию, но уважает надёжных оперативников.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 24, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-james-iron-reed"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован профиль Джеймса Рида и привязан к корпоративным сценариям Militech."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

