-- NPC: canon-npc-fixer-jackie-welles-2020-2093
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-fixer-jackie-welles-2020-2093', 'Jackie Welles — уличный напарник и мерк', '{"metadata": {"id": "canon-npc-fixer-jackie-welles-2020-2093", "title": "Jackie Welles — уличный напарник и мерк", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-20T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-20T00:00:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "fixer", "merc", "heywood", "valentinos"], "topics": ["narrative", "quests", "factions"], "related_systems": ["npc-service", "social-service", "world-service"], "related_documents": [{"id": "canon-npc-fixer-marco-fix-sanchez", "relation": "complements"}, {"id": "canon-scenarios-locations-night-city-heywood-2020-2093", "relation": "references"}, {"id": "canon-factions-gangs-valentinos-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/fixers/jackie-welles-detailed-2020-2093.md", "visibility": "internal", "audience": ["narrative", "gameplay", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Street Narrative Cell", "reviewed_at": "2025-12-20T00:00:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Jackie Welles - ключевой NPC из Cyberpunk 2077, но его профиль отсутствует в структурированной базе знаний.", "goal": "Создать детализированный профиль уличного мерка и фиксера для интеграции в квестовые линии Heywood и Valentinos.", "essence": "Jackie - лояльный напарник с сильными моральными принципами, опытный мерк с мексиканскими корнями, связанный с Valentinos.", "key_points": ["Родом из мексиканской семьи, вырос в Heywood, член Valentinos.", "Специализируется на \\"чистых\\" контрактах - защита, сопровождение, инфильтрация.", "Имеет сильный кодекс чести, отказывается от \\"грязных\\" работ.", "Становится надежным напарником для новых игроков в Night City."]}, "content": {"sections": [{"id": "profile", "title": "Основная информация", "body": "Jackie Welles (Хайме Сантьяго) — 28-летний мерк и фиксер из Heywood, член банды Valentinos. Родился в мексиканской семье иммигрантов,\\nвырос на улицах Night City. Специализируется на \\"чистых\\" контрактах: защита VIP-персон, сопровождение грузов, инфильтрация без жертв.\\nЖивет в скромном доме в Heywood с матерью и сестрой, поддерживает семью через легальные и полулегальные работы.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История", "body": "Родился в 2010 году в семье мексиканских иммигрантов в Heywood. Отец работал на Arasaka, погиб во время корпоративных беспорядков 2025 года.\\nС детства связан с Valentinos - местной бандой, защищающей латиноамериканское сообщество. Начал карьеру мерка в 17 лет, специализируясь на\\nконтрактах средней сложности. К 2020-м годам стал уважаемым фиксером с репутацией надежного и честного исполнителя.\\n\\nВ 2040-х пережил тяжелую травму - потерял напарника в неудачном контракте, что сделало его осторожнее. Стал больше ценить жизнь и\\nотказываться от \\"грязных\\" работ. Поддерживает тесные связи с Valentinos, но работает независимо, помогая новым меркам войти в бизнес.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Основная мотивация - поддержка семьи и сообщества Heywood. Jackie стремится к стабильности и уважению, хочет вывести семью из бедности.\\nЕго философия: \\"Работай чисто, живи долго\\". Отказывается от контрактов, где нужно убивать невинных или работать на корпорации.\\n\\nДолгосрочные цели: накопить достаточно денег для открытия легального бизнеса, возможно, мастерской по ремонту машин или бара.\\nБоится повторить судьбу отца - стать жертвой корпоративных войн. Активно помогает новым меркам, видя в них себя молодого.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и манеры", "body": "Веселый, дружелюбный и лояльный. Говорит с сильным мексиканским акцентом, часто использует испанские слова и выражения.\\nОбладает заразительным смехом и оптимизмом, несмотря на тяжелую жизнь на улицах. Очень семейный - часто упоминает маму и сестру.\\n\\nВ работе профессионален и дисциплинирован, но не скрывает эмоции. Легко заводит друзей, но если предают - становится непримиримым.\\nИмеет кодекс чести: \\"Никогда не предавай напарника, никогда не работай на корпорации, помогай слабым\\".\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность", "body": "Рост около 185 см, мускулистое телосложение. Короткие черные волосы, аккуратная борода. Темные глаза с теплым взглядом.\\nТипичная одежда мерка: джинсы, футболка с принтом, кожаная куртка с нашивками Valentinos. Носит несколько татуировок -\\nна руках символика Valentinos, на шее - крест (подарок матери).\\n\\nИмеет базовые импланты: укрепление мышц, улучшенное зрение. Не гонится за дорогими киберимплантами, предпочитая \\"человечность\\".\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи", "body": "**Valentinos**: Активный член, пользуется уважением за лояльность. Часто помогает банде с \\"чистыми\\" контрактами.\\n\\n**Другие фиксеры**: Дружеские отношения с большинством, но особенно близок с Marco \\"Fix\\" Sanchez. Уважает Dakota Smith за ее независимость.\\n\\n**Семья**: Очень близок с матерью Guadalupe и сестрой Elisa. Регулярно помогает деньгами и поддержкой.\\n\\n**Игрок**: Может стать первым напарником новичка в Night City. Начинает с осторожного доверия, но если игрок показывает лояльность,\\nстановится настоящим другом и наставником.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квестовые линии", "body": "**\\"Первый шаг в Night City\\"**: Jackie помогает новому мерку с первыми контрактами в Heywood. Учит основам ремесла.\\n\\n**\\"Семейные дела\\"**: Игрок помогает Jackie с проблемами семьи - защитой от местных банд или корпоративных сборщиков долгов.\\n\\n**\\"Чистый контракт\\"**: Серия заданий по защите VIP-персон или сопровождению грузов. Jackie отказывается от \\"грязных\\" вариантов.\\n\\n**\\"Наследие Valentinos\\"**: Исследование истории банды и роли Jackie в ней. Может привести к внутренним конфликтам.\\n", "mechanics_links": [], "assets": []}, {"id": "combat_style", "title": "Боевой стиль", "body": "Специализируется на ближнем бое и защите. Предпочитает не убивать, а выводить из строя. Использует тактику \\"защиты и контратаки\\".\\nХорошо работает в команде, всегда прикрывает спину напарнику. В критических ситуациях становится очень эффективным,\\nно после боя часто переживает о последствиях.\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Развитие персонажа", "body": "Может развиваться в трех направлениях:\\n1. **Лояльный напарник**: Становится постоянным партнером игрока, развивает бизнес вместе.\\n2. **Семейный человек**: Фокусируется на семье, уходит из мира мерков.\\n3. **Лидер Valentinos**: Принимает более активную роль в банде, становится ее представителем.\\n\\nВозможна трагическая арка, если игрок предает доверие - Jackie становится антагонистом или уходит из жизни.\\n", "mechanics_links": [], "assets": []}, {"id": "inspiration", "title": "Источники вдохновения", "body": "Основан на Jackie Welles из Cyberpunk 2077. Вдохновлен реальными мексиканскими иммигрантами в США, их культурой и борьбой за достойную жизнь.\\nКодекс чести напоминает самурайскую мораль, адаптированную к уличной жизни Night City. Музыкальные вкусы - латиноамериканская музыка,\\nлюбимая песня \\"Viva La Vida\\" в исполнении Coldplay.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Marco Fix Sanchez — профиль", "link": "marco-fix-sanchez-detailed-2020-2093.yaml"}, {"title": "Valentinos — фракция", "link": "../../../../factions/gangs/valentinos-2020-2093.yaml"}, {"title": "Heywood 2020-2093", "link": "../../../../scenarios/locations/night-city/heywood-2020-2093.yaml"}], "decisions": []}, "implementation": {"github_issue": 1914, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-20", "author": "content_writer", "changes": "Создан детализированный профиль Jackie Welles для интеграции в квестовые линии."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

