INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-fixer-wakako-okada-2035-2090', 'Wakako Okada — фиксер Japantown', '{"metadata": {"id": "canon-npc-fixer-wakako-okada-2035-2090", "title": "Wakako Okada — фиксер Japantown", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:22:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "fixer", "japantown"], "topics": ["narrative", "tradition"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-scenarios-locations-night-city-japantown-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/fixers/wakako-okada-2035-2090.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Narrative NPC Cell", "reviewed_at": "2025-11-06T11:22:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Профиль Wakako Okada был в Markdown и требовал миграции в YAML базу NPC.", "goal": "Зафиксировать ключевую фигуру Japantown, контролирующую Tyger Claws и японские корпорации.", "essence": "Вакако — уважённая матриарх района, сочетающая традиции и беспощадность в подборе контрактов.", "key_points": ["Старейший активный фиксер Night City, известный безупречной репутацией и требованиями к уважению.", "Квест «Честь Japantown» концентрируется на наказании предательства и восстановлении статуса клана.", "Управляет сетью из Pachinko parlor, оставаясь связной Arasaka и Tyger Claws."]}, "content": {"sections": [{"id": "history", "title": "История", "body": "Родившись в Японии и переехав в США в 1970-х, Wakako построила бизнес Japantown, превратив его в узел между Tyger Claws,\\nArasaka и независимыми операторами. Её репутация сформировалась на соблюдении традиций и железной дисциплине.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Холодная, вежливая и требовательная. Ожидает безусловного уважения, придерживается кодекса чести и не терпит некомпетентности.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квесты", "body": "«Честь Japantown» требует восстановить репутацию клана после предательства агента, подчеркивая традиционные ценности Wakako.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"Уважение — это валюта, которую нельзя купить. Можно только заработать.\\" — ключевая фраза, отражающая мировоззрение фикcера.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Japantown 2020-2093", "link": "../../../../scenarios/locations/night-city/japantown-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "npc_team", "changes": "Создан профиль фикcера Wakako Okada."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

