INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-adam-smasher', 'Адам Смешер', '{"metadata": {"id": "canon-npc-adam-smasher", "title": "Адам Смешер", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.1.0", "last_updated": "2025-11-11T22:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "arasaka", "boss"], "topics": ["characters", "corporate-conflict"], "related_systems": ["narrative-service", "gameplay-service", "world-service"], "related_documents": [{"id": "canon-corporate-arasaka-profile", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/adam-smasher.md", "visibility": "internal", "audience": ["narrative", "systems-design", "encounter-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Адама Смешера существовал в Markdown и не фиксировал системные зависимости для боевого босса Arasaka.", "goal": "Структурировать ключевые характеристики, мотивацию и механики NPC для интеграции в корпоративные квесты и рейды.", "essence": "«Полностью кибернетизированный палач Arasaka, символ корпоративного террора и проверки билдов игроков».", "key_points": ["Полная конверсия тела и лояльность к Arasaka задают образ «неостановимого» босса.", "Используется как кульминация корпоративных и уличных конфликтов, усиливая ставку кризисных событий.", "Требует особых боевых фаз с невосприимчивостью, что влияет на дизайн encounter-систем."]}, "content": {"sections": [{"id": "identity-profile", "title": "Профиль и роль", "body": "- Корпоративный боевой актив Arasaka, появляющийся при эскалации конфликтов.\\n- Локации: защищённые комплексы Arasaka в City Center и стратегические объекты корпорации.\\n- Используется как инструмент устрашения и контроль над мятежами.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/corporations/arasaka-2020-2093.yaml"], "assets": []}, {"id": "appearance-and-augmentation", "title": "Внешний вид и кибернетика", "body": "- Почти полная замена органики боевым корпусом; тяжёлая броня и индустриальные приводы.\\n- Встроенные стабилизаторы отдачи, тактическая оптика и системы подавления.\\n- Визуальные элементы: тёмный металл, красные акценты, маркировка Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "psychology", "title": "Мотивация и психология", "body": "- Абсолютное подчинение приказам, стремление к доминированию в бою.\\n- Боязнь потери боеспособности и статуса; желает дальнейших улучшений.\\n- Поведение: холодный инструмент насилия, минимальная эмпатия.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношение к фракциям", "body": "- Лоялен Arasaka; воспринимает Militech как демонстрационные цели.\\n- Игнорирует уличные банды, пока они не мешают корпоративным операциям.\\n- Для игрока выступает как враждебный encounter, чутко реагирующий на саботаж корпорации.\\n", "mechanics_links": ["canon/narrative/factions/faction-matrix.yaml"], "assets": []}, {"id": "encounter-design", "title": "Геймплей и использование", "body": "- Служит боссом высокой сложности: фазы неуязвимости, AoE подавление, проверка билдов.\\n- Квестовые крючки: «Железная рука», «Сердце стали» и корпоративные рейды.\\n- Инвентарь: тяжёлый пулемёт, интегрированные клинки, доступ к корпоративным меткам.\\n", "mechanics_links": ["mechanics/combat/combat-boss-design.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Таймлайн 2060–2077 (корпоративные войны)", "link": "shared/docs/knowledge/canon/lore/_03-lore/timeline/2060-2077-pre-modern.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Адама Смешера в YAML и добавление геймплейных связей."}, {"version": "1.0.0", "date": "2025-11-05", "author": "narrative_team", "changes": "Черновой профиль NPC в Markdown."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

