-- NPC: npc-arthur-jenkins
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-arthur-jenkins', 'Артур Дженкинс — корпоративный менеджер Arasaka', '{"metadata": {"id": "npc-arthur-jenkins", "title": "Артур Дженкинс — корпоративный менеджер Arasaka", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "arasaka", "corporate-intrigue"], "topics": ["npc-biographies", "corpo-storylines"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-narrative/main-story/framework", "relation": "references"}, {"id": "canon-narrative/scenarios/factions/corporations/arasaka-2020-2093", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/arthur-jenkins.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Биография NPC Артура Дженкинса хранилась в Markdown и не была связана с корпоративными сюжетами.", "goal": "Структурировать данные о Дженкинсе для использования в корпоративной арке Arasaka и связанных квестах.", "essence": "Амбициозный менеджер среднего звена, манипулирующий игроком ради продвижения по службе.", "key_points": ["Представитель Arasaka, застрявший в среднем звене и готовый к интригам.", "Внешность и импланты соответствуют корпоративному стандарту, подчёркивая стресс и амбиции.", "Стремится использовать игрока как инструмент, исход зависит от решений.", "Слаб в бою, но силён в социальных манипуляциях и доступе к ресурсам корпорации."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и роль", "body": "Артур Дженкинс — менеджер среднего звена Arasaka (ID `npc-arthur-jenkins`), работающий в башне Arasaka\\nв City Center. Возраст 35–45 лет, корпоративное происхождение, разведён. Его активные годы 2068–2077,\\nжизненный период 2038–2077. Образ — ухоженный офисный работник в строгих костюмах с фирменными акцентами Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "Поддерживает корпоративный стандарт: минимальные визуальные модификации, коммуникационные импланты,\\nбиомониторы и когнитивные усиления. Оружие носит редко (корпоративный пистолет), полагается на бронированный\\nкостюм и доступ к защищённым устройствам связи.\\n", "mechanics_links": [], "assets": []}, {"id": "biography", "title": "Биография и арка", "body": "Вырос в корпоративной среде и с детства готовился к карьере в Arasaka. Быстро поднялся благодаря агрессии\\nи готовности идти по головам, но застрял в среднем звене. Развод и внутренние интриги усилили стресс.\\nПытается использовать игрока для устранения конкурентов; финал зависит от решений — от продвижения до гибели.\\n", "mechanics_links": ["canon/narrative/sid-endings/sid-system.yaml"], "assets": []}, {"id": "motivations", "title": "Мотивация и психология", "body": "Мотивирован карьерным ростом, властью и признанием. Боится застоя, увольнения и возмездия. Характер —\\nхолерик, нервозен, беспринципен и расчётлив. Любимые фразы подчеркивают прагматизм: «В корпорации нет друзей,\\nтолько союзники и конкуренты».\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения и влияние", "body": "Лоялен Arasaka из расчёта, Militech считает врагами, NCPD использует по связям. Не имеет истинных друзей,\\nокружён соперниками. С игроком взаимодействует манипулятивно: поручения дают временные бонусы, отказ или\\nраскрытие интриг ведут к враждебности.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Геймплейная роль", "body": "Служит вводным NPC корпоративной арки: квесты «Корпоративная лестница» и «Цена амбиций». Боевые навыки низкие,\\nхакерские и социальные выше среднего. Предоставляет контакты Arasaka, но стремится контролировать ситуацию.\\n", "mechanics_links": ["mechanics/combat/combat-shooting-advanced.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 30, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-arthur-jenkins"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертирован профиль Артура Дженкинса в формат YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

