INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-alt-cunningham', 'Альт Каннингем', '{"metadata": {"id": "canon-npc-alt-cunningham", "title": "Альт Каннингем", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T22:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "netrunner", "soulkiller"], "topics": ["characters", "cyberspace"], "related_systems": ["narrative-service", "net-service", "quest-engine"], "related_documents": [{"id": "canon-npc-johnny-silverhand", "relation": "influences"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/alt-cunningham.md", "visibility": "internal", "audience": ["narrative", "systems-design", "net-operations"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание Альт Каннингем оставалось в Markdown и не привязывало цифровой NPC к системам киберпространства и Soulkiller.", "goal": "Сконсолидировать лор, мотивацию и механики цифрового сознания для сюжетов Net и борьбы с Arasaka.", "essence": "«Легендарный нетраннер, ставший цифровым призраком и союзником против Soulkiller».", "key_points": ["Полностью цифровое существование определяет уникальное взаимодействие игрока с NET.", "Мотивация строится на противостоянии Arasaka и защите свободы сознаний.", "Альт выдаёт квесты и услуги, связанные с Soulkiller, что влияет на прогресс нетраннеров."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и функция", "body": "- Автор программы Soulkiller, после похищения Arasaka существует как цифровое сознание.\\n- Локации: распределённые узлы NET; взаимодействует через избранные интерфейсы.\\n- Используется для сюжетов «освобождения сознаний» и экспозиций истории NET.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/netfactions/netwatch-voodoo-boys-2020-2093.yaml"], "assets": []}, {"id": "digital-form", "title": "Цифровой образ и способности", "body": "- Не имеет физического тела; аватар динамически меняет форму и цветовую палитру.\\n- Носит массивы знаний о Soulkiller и маршрутах за Blackwall.\\n- Может активировать защитные протоколы, усиливающие игроков в сетевых миссиях.\\n", "mechanics_links": ["mechanics/combat/combat-cyberpsychosis.yaml"], "assets": []}, {"id": "motivations", "title": "Мотивация и эмоциональный профиль", "body": "- Стремится разрушить монополию Arasaka на Soulkiller и защитить свободу сознаний.\\n- Готова помогать игрокам, если их действия направлены против корпоративного контроля.\\n- Опасается повторного порабощения и использования её кода против невинных.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношения", "body": "- Дружба и наследие чувств с Джонни Сильверхендом формируют эмоциональные сценари.\\n- Враждует с Arasaka; нейтральна к другим фракциям, пока они не угрожают NET.\\n- С игроками строит отношения через квесты киберпространства и помощь нетраннерам.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Геймплейное использование", "body": "- Выдаёт цепочки «Киберпространство», «Soulkiller» и «Свобода сознаний».\\n- Предоставляет услуги: анализ сетей, эксклюзивные знания, Net-перки.\\n- Не участвует в бою напрямую, но усиливает игроков и открывает безопасные зоны NET.\\n", "mechanics_links": ["mechanics/net/net-runner-advanced.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Soulkiller — технический обзор", "link": "shared/docs/knowledge/implementation/backend/cyberspace/soulkiller-overview.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Альт Каннингем в YAML и добавление системных связей."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

