INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-citizen-street-artist-2070s-westbrook', 'Уличный художник (Westbrook, 2070-е)', '{"metadata": {"id": "npc-common-citizen-street-artist-2070s-westbrook", "title": "Уличный художник (Westbrook, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "artist", "westbrook"], "topics": ["street_art", "urban_culture"], "related_systems": ["narrative-service", "world-simulation"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/citizens/street-artist-2070s-westbrook.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно закрепить образ уличного художника, отражающего творческую сцену Westbrook.", "goal": "Описать поведение и использование NPC в квестах о городском искусстве и репутации районов.", "essence": "Художник создаёт граффити, ведёт культурные акции и делится историями о районе.", "key_points": ["Активен в 2070-х, реагирует на состояние улиц и действия игроков.", "Может стать проводником в субкультуры и нелегальные арт-ивенты.", "Поддерживает атмосферу живого мегаполиса и его контркультур."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Артист, использующий AR-слои и неоновые краски для масштабных граффити. Работает ночью, опирается на сеть\\nхудожников и музыкантов в Westbrook, стремится оставить послание на каждой стене.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "activities", "title": "Деятельность", "body": "- Создание интерактивных граффити, реагирующих на события.\\n- Организация тайных выставок и уличных перформансов.\\n- Ведение блога о городской культуре и истории района.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "interactions", "title": "Взаимодействия с игроком", "body": "- Предлагает квесты на защиту арт-объектов или распространение работ по городу.\\n- Просит добыть редкие материалы или доступ к закрытым площадкам.\\n- Рассказывает истории о влиянии корпораций на творческую сцену.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "dialogue_sample", "title": "Образец реплики", "body": "«Город — это холст. Смотри внимательнее, и увидишь, кто им управляет.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль уличного художника Westbrook в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

