INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-coach-fred', 'Coach Fred — наставник по фистфайту', '{"metadata": {"id": "canon-npc-coach-fred", "title": "Coach Fred — наставник по фистфайту", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "coach", "combat"], "topics": ["narrative", "tutorial"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-scenarios-locations-night-city-watson-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/coach-fred.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль тренера по фистфайту существовал только в Markdown и не был включен в YAML базу NPC.", "goal": "Стандартизировать сведения о наставнике ближнего боя для учебных механик и второстепенных квестов.", "essence": "Coach Fred — дружелюбный циник, обучающий игрока основам рукопашного боя и предлагающий испытания в Watson.", "key_points": ["Работа с игроком через учебные сессии и ставки на бои.", "Предоставляет мотивации и советы новичкам.", "Имеет независимый статус и базируется в спортзале Watson."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Coach Fred проводит тренировки в спортивном зале Watson, наставляя новичков и профессионалов. Он сочетает циничное чувство юмора\\nс искренним желанием помочь улучшить навыки ближнего боя.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Персонаж запускает туториалы рукопашных боёв, организует испытания и ставки, знакомя игрока с системой фистфайтов и парирования.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"Сначала стойка, потом понты.\\" — ключевая реплика, подчёркивающая его подход к обучению.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Watson 2020-2093", "link": "../../../../scenarios/locations/night-city/watson-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-05", "author": "npc_team", "changes": "Создан профиль Coach Fred."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

