INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-brigitte', 'Мамана Брижит — лидер Voodoo Boys', '{"metadata": {"id": "npc-brigitte", "title": "Мамана Брижит — лидер Voodoo Boys", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "voodoo-boys", "netrunner"], "topics": ["npc-biographies", "net-operations"], "related_systems": ["narrative-service", "quest-service", "realtime-service"], "related_documents": [{"id": "canon-narrative/scenarios/factions/netfactions/netwatch-voodoo-boys-2020-2093", "relation": "complements"}, {"id": "mechanics/combat/combat-hacking-types", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/brigitte.md", "visibility": "internal", "audience": ["concept", "narrative", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Маманы Брижит существовал в Markdown и не был связан с документацией Voodoo Boys.", "goal": "Зафиксировать ключевые данные о лидере Voodoo Boys для сюжетов Pacifica и операций за Blackwall.", "essence": "Стратег и архитектор сетевых операций, отстаивающая автономию сообщества и контроль над информацией.", "key_points": ["Лидер сетевого подполья Pacifica, спокойная и расчётливая.", "Управляет глубинными операциями в NET и планирует проникновения за Blackwall.", "Строит альянсы с Placide и рассматривает связи с Alt Cunningham.", "Даёт доступ к высокоуровневым нет-рейдам и уникальному софту."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и роль", "body": "Мамана Брижит (ID `npc-brigitte`) руководит Voodoo Boys из Pacifica, контролируя сетевые узлы и подпольные\\nканалы связи. Образ сочетает этнические мотивы и кибернетику; манера — спокойная, уверенная, осторожная.\\n", "mechanics_links": [], "assets": []}, {"id": "expertise", "title": "История и экспертиза", "body": "Имеет глубокую экспертизу в NET и доступ к контактам за Blackwall. Формирует стратегии проникновения,\\nзащиты и разведки данных. Влияет на долгосрочные планы Voodoo Boys и определяет цели операций против NetWatch\\nи корпораций.\\n", "mechanics_links": ["mechanics/world/events/world-events-framework.yaml"], "assets": []}, {"id": "motivations", "title": "Мотивация и цели", "body": "Стремится к автономии сообщества, контролю информации и минимизации влияния корпораций. Видит в NET\\nпространство свободы и исследует союзы с цифровыми сущностями, чтобы сохранить независимость Pacifica.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения и влияния", "body": "Делегирует задачи Placide, рассматривает Alt Cunningham как потенциального союзника для высокоуровневых операций.\\nКонфликтует с NetWatch и корпорациями, но готова к осторожным переговорам, если это укрепляет позиции Voodoo Boys.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Геймплейная роль", "body": "Активирует высокоуровневые нет-рейды и многошаговые операции: получение доступа к редкому софту, схемам защиты,\\nпрокси-запускам за Blackwall. Награды включают уникальные программы и усиления для нетраннеров.\\n", "mechanics_links": ["mechanics/combat/combat-hacking-combat-integration.yaml"], "assets": []}, {"id": "dialogue", "title": "Диалоговые акценты", "body": "Характерные реплики: «Сеть помнит всё. Мы — её хранители.»; «Корпорации не контролируют то, что им не принадлежит.»\\nКоммуникация сдержанная, она проверяет намерения игрока перед выдачей доверительных поручений.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 29, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-brigitte"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Канонический профиль Маманы Брижит перенесён в YAML и связан с документами Voodoo Boys."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

