INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-lucy', 'Lucy — нетраннер Edgerunners', '{"metadata": {"id": "canon-npc-lucy", "title": "Lucy — нетраннер Edgerunners", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "netrunner", "edgerunners"], "topics": ["narrative", "drama"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-npc-kiwi", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/lucy.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Lucy из Edgerunners требовал стандартизированного YAML представления.", "goal": "Зафиксировать трагичного нетраннера с высоким уровнем навыков и эмоциональным прошлым.", "essence": "Lucy — замкнутая специалистка по нетрану, чьи операции сопряжены с риском и глубокой личной историей.", "key_points": ["Проводит высокоуровневые нет-операции с акцентом на стелс и сигнатурные навыки.", "Предпочитает одиночество и минимальное общение, что влияет на взаимодействия с игроком.", "Сюжетные линии затрагивают травмы и попытки оторваться от прошлых контрактов."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Lucy остаётся закрытой и осторожной, избегая длинных бесед. Её прошлое наполнено травмами, что заставляет её держаться подальше\\nот людей и полагаться на себя.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "В миссиях с Lucy игрок принимает участие в сложных сетевых операциях, где важно скрытность и знание систем безопасности.\\n", "