INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-dexter-deshawn', 'Декстер ДеШон', '{"metadata": {"id": "canon-npc-dexter-deshawn", "title": "Декстер ДеШон", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T22:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "fixer", "night-city"], "topics": ["characters", "underworld"], "related_systems": ["narrative-service", "quest-engine", "economy-service"], "related_documents": [{"id": "canon-narrative-fixers-network", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/dexter-deshawn.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Декстера ДеШона находился в Markdown и не описывал системные крючки для фиксерских операций.", "goal": "Описать характер, связи и игровые функции фиксерa, обеспечивая поддержку сюжетов Night City и экономики заказов.", "essence": "«Харизматичный, безжалостный посредник, который организует рискованные операции ради влияния».", "key_points": ["Представляет нейтральный центр силы, работающий с любыми платёжеспособными клиентами.", "Ведёт игрока по цепочкам «опасных заданий», влияя на репутацию и доступ к подконтрольному подполью.", {"Взаимодействие определяется успешностью операций": "доверие и награды растут с прогрессом игрока."}]}, "content": {"sections": [{"id": "identity", "title": "Профиль и позиционирование", "body": "- Фиксер высокого уровня, выросший на улицах и превративший опыт в бизнес.\\n- Поддерживает сеть контактов среди наёмников, корпораций и банд.\\n- Используется как стартовая точка для сюжетов о влиянии и подпольных сделках.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/gangs/gangs-2020-2093.yaml"], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Высокий, мускулистый мужчина с подчёркнутым статусом.\\n- Отдаёт предпочтение дорогим костюмам с скрытой бронёй.\\n- Импланты: связь, анализ данных, усиленные боевые рефлексы.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и мотивация", "body": "- Черты: профессионализм, харизма, амбициозность, отсутствие сентиментальности.\\n- Мотивация: деньги и влияние через успешные операции.\\n- Опасается падения репутации и потери контроля над сетью.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношения", "body": "- Нейтрален к большинству фракций, взаимодействует со всеми, кто платит.\\n- Конкурирует с другими фиксерaми; ценит лучших наёмников и лояльных клиентов.\\n- С игроком строит отношения через выполнение заданий и соблюдение деловой этики.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "gameplay", "title": "Игровое применение", "body": "- Выдаёт цепочки «Опасные задания», «Сложные операции», «Подпольный мир».\\n- Услуги: организация операций, доступ к редким контрактам, информация о подполье.\\n- Навыки: высокие социальные навыки, хорошие боевые рефлексы, базовые техно-хакерские знания.\\n", "mechanics_links": ["mechanics/economy/player-market/player-market-core.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Список важных NPC", "link": "shared/docs/knowledge/canon/narrative/npc-lore/important/important-npcs-list.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Декстера ДеШона в YAML и описание фиксерских механик."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

