INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-hanako-arasaka', 'Hanako Arasaka — профиль NPC', '{"metadata": {"id": "npc-hanako-arasaka", "title": "Hanako Arasaka — профиль NPC", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.2.0", "last_updated": "2025-11-12T00:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "arasaka", "diplomacy"], "topics": ["npc-lore", "corporate-politics"], "related_systems": ["narrative-service", "economy-service", "social-service"], "related_documents": [{"id": "npc-yorinobu-arasaka", "relation": "conflicts"}, {"id": "mq-002-corporate-branch", "relation": "drives"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/hanako-arasaka.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Hanako Arasaka находился вне структурированной БЗ и не описывал связи с механиками NECPGAME.", "goal": "Сформировать канонический файл для корпоративной ветки сюжета и Live Ops, фиксирующий дипломатическое влияние Hanako.", "essence": "«Стратег Arasaka, удерживающий традиции и выстраивающий новый корпоративный порядок через дипломатические альянсы».", "key_points": ["Hanako — ключевой куратор корпоративной ветки и главный переговорщик Arasaka.", "Её решения балансируют между традициями и модернизацией, напрямую влияя на концовки.", "Взаимодействие с игроком завязано на выполнении корпоративных миссий и соблюдении кодекса корпорации."]}, "content": {"sections": [{"id": "identity", "title": "Идентичность и функция", "body": "- Полное имя: Hanako Arasaka (荒坂花子), ID `npc-hanako-arasaka`.\\n- Роль: стратегический директор Arasaka, наследница Saburo, активна 2070–2093.\\n- Локации: башня Arasaka, закрытые резиденции Токио и Charter Hill, VIP-залы клуба Afterglow.\\n- Фокус: восстановление глобального влияния корпорации и контроль Night City через дипломатические решения.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и модификации", "body": "- Изящная женщина 40–50 лет с безупречным самоконтролем и минимальной мимикой.\\n- Синтез традиционного kimono-tech и корпоративной высокой моды с символикой Arasaka.\\n- Использует элитные нейроинтерфейсы, защитные протоколы Black ICE, биомониторы премиум-класса; модификации скрыты.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "Биография и мотивация", "body": "- Образование сочетает традиционные дисциплины и корпоративную стратегию.\\n- Координировала восстановление Arasaka после кризиса 2077, запустила программу «Neo-Corporate» для Night City.\\n- Мотивация: сохранить наследие Saburo и построить обновлённый порядок, контролируя ресурсы и устраняя конкуренцию Militech.\\n- Страхи: внутренние предательства, повторение ошибок Yorinobu, потеря контроля над Relic; желание — признание как законной наследницы.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и поведение", "body": "- Хладнокровный стратег, придерживающийся кодекса чести и педагогики чайных церемоний.\\n- Манера речи формальная и уважительная, но сдержанно-покровительственная; акцент мягкий японский.\\n- Любимые фразы: «Честь — это валюта времени», «Arasaka не бросает слова на ветер».\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и дипломатия", "body": "- Фракции: абсолютная лояльность Arasaka, холодная конкуренция с Militech, нейтральные контакты с Валентинос и Fixers Guild.\\n- NPC: Saburo как идеал, конфликт с Yorinobu, дипломатические каналы с Padre, напряжённое соперничество с James \\"Iron\\" Reed.\\n- Игрок: начинает с осторожного интереса; повышение доверия требует соблюдения корпоративного кодекса и успешных миссий.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/corporations/arasaka-2020-2093.yaml"], "assets": []}, {"id": "gameplay", "title": "Игровое влияние", "body": "- Куратор корпоративной линии (`mq-002`, `mq-003`, `mq-101`), открывает доступ к вооружениям и имплантам Arasaka.\\n- Социальные навыки максимальные, боевые — средние (опора на охрану), хакерские — опосредованные.\\n- Влияет на `gameplay-service`, `social-service` и `world-service`, задавая корпоративные перки и сценарии Live Ops.\\n", "mechanics_links": ["canon/narrative/quests/main/mq-002.yaml", "canon/narrative/quests/main/mq-003.yaml"], "assets": []}, {"id": "dialogue", "title": "Диалоговые модули", "body": "- Приветствия варьируются от «Arasaka ценит точность и уважение» до «Ваши действия вызывают вопросы».\\n- Диалоги раскрывают корпоративную философию, контроль Night City и выгодные сделки.\\n- Спецреплики для `mq-002`, `mq-003` и финалов корпоративной арки отражают выбранную концовку.\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Арка и концовки", "body": "- Арка строится на дилемме модернизации против традиций; исход зависит от решений игрока.\\n- Возможные финалы: корпоративное господство вместе с игроком, реформа через альянс, поражение от Militech или внутренних заговоров.\\n- Запускает цепочку «Наследие империи» в поздних лигах и влияет на глобальный рейтинг Arasaka.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Корпоративные квесты", "link": "canon/narrative/quests/main/mq-main-arc.yaml"}, {"title": "Список важных NPC", "link": "canon/narrative/npc-lore/important-npcs-list.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.2.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Hanako Arasaka в YAML и детализация корпоративных сценариев."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

