INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-fingers-robert', 'Фингерс Роберт — риппердок сомнительной репутации', '{"metadata": {"id": "npc-fingers-robert", "title": "Фингерс Роберт — риппердок сомнительной репутации", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "ripperdoc", "nightlife"], "topics": ["npc-biographies", "shady-business"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "canon-narrative/npc-lore/factions/gangs/mox/madam-2065-2080", "relation": "references"}, {"id": "mechanics/economy/economy-advertising-platform", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/fingers-robert.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль риппердока Фингерса существовал в Markdown и не описывал механики серых услуг и репутационных рисков.", "goal": "Структурировать информацию о персонаже для сюжетов Westbrook и моральных дилемм игроков.", "essence": "Манерный риппердок, зарабатывающий на дешёвых и опасных имплантах и балансирующий между The Mox и клиентами.", "key_points": ["Работает в подпольных клиниках Japantown, предлагает низкосортные импланты.", "Известен сомнительными схемами и скандалами вокруг клиентов.", "Стремится к прибыли ценой репутации, избегает внимания полиции.", "С ним связаны квесты на разоблачение, покровительство и выбор моральных последствий."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Фингерс Роберт (`npc-fingers-robert`) — независимый риппердок Westbrook. Стиль пёстрый и вызывающий,\\nповедение манерное с притворной заботой. Клиентов привлекает доступностью и обещаниями «дешёвых чудес».\\n", "mechanics_links": [], "assets": []}, {"id": "operations", "title": "Деятельность и схемы", "body": "Известен серыми схемами: экономит на стерильности, устанавливает восстановленные импланты, хранит компромат.\\nЕго услуги часто приводят к осложнениям, что создаёт сюжетные конфликты и расследования.\\n", "mechanics_links": ["mechanics/economy/economy-crafting.yaml"], "assets": []}, {"id": "motivations", "title": "Мотивация", "body": "Основная цель — прибыль и удержание клиентской базы через дефицит и страх. Он сохраняет сеть поставщиков и\\nпредлагает «варианты подешевле», скрывая реальные риски.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения", "body": "С The Mox отношения напряжённые, но они терпят его ради доступа к услугам. Полицию избегает, пользуясь\\nподкупами и перемещением клиник. Сталкивается с журналистами и активистами, расследующими его практики.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Игровое применение", "body": "Игрок может разоблачить Фингерса, покрыть его или воспользоваться услугами ради дешёвых модификаций. Награды —\\nсомнительные импланты, информация о подпольных клиентах, доступ к серым рынкам.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "Характерные фразы: «Красота — боль, детка. Вопрос — сколько вынесешь?»; «Есть вариант подешевле... и вариант\\nдля тех, кто ценит качество.» Тон общений приторный, он всегда предлагает «особые условия».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 26, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-fingers-robert"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертирован профиль Фингерса Роберта в YAML с описанием серых практик."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

