-- NPC: common-enemy-maelstrom-tech-2070s-watson-001
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-enemy-maelstrom-tech-2070s-watson-001', 'Техник Maelstrom (Watson, 2070-е)', '{"metadata": {"id": "common-enemy-maelstrom-tech-2070s-watson-001", "title": "Техник Maelstrom (Watson, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "maelstrom", "enemy"], "topics": ["gang-combat", "encounter-design"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/maelstrom-tech-2070s-watson.md", "visibility": "internal", "audience": ["concept", "combat"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание техника Maelstrom было записано в Markdown и не влияло на дизайн встреч в Watson.", "goal": "Структурировать профиль врага с упором на ловушки и поддержку турелей для миссий в промзоне.", "essence": "Инженер банды насыщает арену минами и дронами, заставляя игрока искать противодействие через EMP.", "key_points": ["Использует самодельные турели и мины, прикрывая продвижение отряда.", "Слаб к EMP-оружию и целевым взломам, что открывает окна для нападения.", "Поддерживает других бойцов Maelstrom, ремонтируя броню и дронов прямо в бою."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Техник Maelstrom сопровождает рейды банды в Watson и отвечает за установку ловушек. У него массивный\\nнабор киберимплантов и модифицированные глаза для отслеживания движения. Чаще встречается на складах и\\nзаводах у Нортсайдского порта.\\n", "mechanics_links": [], "assets": []}, {"id": "combat_behaviour", "title": "Боевой паттерн", "body": "Перед схваткой расставляет мины и активирует стационарные турели. В бою держится за укрытием, управляет\\nдронами и усиливает броню союзников. При снижении здоровья запускает дымовые завесы для отхода.\\n", "mechanics_links": ["mechanics/combat/combat-training-systems.yaml"], "assets": []}, {"id": "weaknesses", "title": "Слабости и контрмеры", "body": "- EMP-гранаты и импульсные винтовки мгновенно отключают его дроны.\\n- Быстрые взломы позволяют перегрузить турели и развернуть их против Maelstrom.\\n- Ограниченная мобильность делает его уязвимым для фланговых атак и высоких точек.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Заказ fixera на нейтрализацию инженера Maelstrom, блокирующего торговые маршруты Watson.\\n- Инфильтрация склада с целью украсть чертежи ловушек и перенастроить оборону против банды.\\n- Контакт с службу безопасности корпорации, которая хочет нанять техника для нелегальных задач.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль техника Maelstrom в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

