INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-hiroshi-tanaka', 'Хироши Танака', '{"metadata": {"id": "canon-npc-hiroshi-tanaka", "title": "Хироши Танака", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T23:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "arasaka", "corporate"], "topics": ["corporate-politics", "recruitment"], "related_systems": ["narrative-service", "quest-engine", "reputation-system"], "related_documents": [{"id": "canon-npc-rogue", "relation": "contrasts"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/hiroshi-tanaka.md", "visibility": "internal", "audience": ["narrative", "encounter-design", "faction-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание корпоративного агента Arasaka существовало в Markdown и не поддерживало сюжетные ветки вербовки и лояльности.", "goal": "Стандартизировать профиль Хироши Танакы для использования в квестах корпораций и сюжетах выбора фракции.", "essence": "Лояльный агент Arasaka, вербующий лучших специалистов и оценивающий игроков по корпоративным стандартам.", "key_points": ["Представляет строгий корпоративный подход и требует доказанной лояльности.", "Управляет наймом и доступом к заданиям Arasaka, влияя на репутацию игрока.", "Враждебен к конкурентам и бандам, сотрудничает только в рамках корпоративной выгоды."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Агент корпорации Arasaka, курирующий подбор кадров и контакт с независимыми наёмниками.\\n- Локация: башня Arasaka в центре Night City, официальные приёмы и закрытые переговорные.\\n- Отвечает за заключение контрактов и повышение статуса лояльных исполнителей.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Мужчина 40–45 лет, подтянутая фигура, аккуратная корпоративная стрижка.\\n- Импланты связи, аналитики и безопасности скрыты под строгим костюмом.\\n- Цветовая палитра: тёмно-синие и чёрные оттенки с символикой Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и мотивация", "body": "- Родом из Японии, прошёл корпоративные академии Arasaka и посвятил карьеру защите интересов корпорации.\\n- Участвовал в операциях по расширению влияния, работает с наёмниками и подрядчиками в Night City.\\n- Мотивирован ростом влияния Arasaka, набором лучших кадров и нейтрализацией конкурентов.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и поведение", "body": "- Профессионален, лоялен и требователен; строго следует процедурам и ожидает того же от партнёров.\\n- Речь формальная, с акцентом на дисциплину и результат; акцент заметно японский.\\n- Личные интересы вращаются вокруг корпоративной политики и укрепления вертикали власти Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношение к фракциям", "body": "- Дружелюбен к Arasaka и её союзникам, оказывает поддержку сотрудникам с высокой лояльностью.\\n- Враждует с Militech и уличными бандами, рассматривая их как угрозу корпоративным операциям.\\n- Оценивает игроков по их выбору фракции, выполнению задач и соответствию корпоративным ожиданиям.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Цепочки квестов: «Выбор корпорации», «Корпоративные операции», «Лояльность».\\n- Сервисы: доступ к корпоративному снаряжению, уникальные контракты, повышение статуса внутри Arasaka.\\n- Навыки: высокие социальные и технические показатели, средние боевые и хакерские способности.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Список важных NPC", "link": "../important-npcs-list.md"}, {"title": "Базовые диалоги NPC", "link": "../../dialogues/npc-dialogues-basic.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Хироши Танакы в YAML и фиксация корпоративных сюжетных ролей."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

