-- NPC: npc-padre-ibarra
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-padre-ibarra', 'Себастьян «Padre» Ибарра — патриарх Heywood', '{"metadata": {"id": "npc-padre-ibarra", "title": "Себастьян «Padre» Ибарра — патриарх Heywood", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.2.0", "last_updated": "2025-11-12T01:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "fixer", "heywood"], "topics": ["npc-lore", "street-politics"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "canon-narrative-quests-street-index", "relation": "drives"}, {"id": "faction-valentinos", "relation": "leads"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/padre-ibarra.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Padre хранился в Markdown и не фиксировал системные связи с уличной веткой, семейными квестами и социальными событиями.", "goal": "Сформировать структурированный YAML-документ, описывающий влияние Padre на Heywood, его мотивации и игровые функции.", "essence": "«Уличный патриарх Heywood, оберегающий честь района и направляющий игроков по кодексу улиц.»", "key_points": ["Зафиксированы ключевые события биографии и мотивация защиты района.", "Добавлены связи с фракциями Valentinos, корпорациями и игроком.", "Определены квестовые хуки, бонусы и финальные ветви."]}, "content": {"sections": [{"id": "identity", "title": "Идентичность и зона контроля", "body": "- Имя: Sebastian «Padre» Ibarra, ID `npc-padre-ibarra`.\\n- Роль: фиксёр Heywood, наставник Valentinos, посредник между улицей и корпорациями.\\n- Локации: часовня San Domingo, мобильные офисы Heywood, частные встречи в районах Найт-Сити.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и стиль", "body": "- Возраст 60+, массивные киберруки с золотыми вставками, кисточки неоновых чёток.\\n- Тёмный костюм с религиозными элементами, цилиндрические импланты подсвечников.\\n- Бронированный седан с скрытыми отсекателями, кибернетические чётки как нейроключи.\\n", "mechanics_links": [], "assets": []}, {"id": "biography", "title": "История и наследие", "body": "- Вырос в Heywood, от посланника до влиятельного фиксёра и миротворца.\\n- Организовал «Padre Logistics», обеспечил перемирие Valentinos и 6th Street (2069), сотрудничал с NCPD без потери независимости.\\n- Сегодня удерживает баланс сил, запускает новые поколения street-операторов.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и страхи", "body": "- Главная цель: сохранить честь и безопасность Heywood, защитить «своих».\\n- Дополнительные мотивы: наставничество, справедливые сделки, удержание традиций Valentinos.\\n- Страхи: корпоративная зачистка, распад уличного кодекса, предательство учеников.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и политика", "body": "- Фракции: наставник Valentinos, дипломат с Maelstrom, прагматичный контакт с NCPD, осторожное сотрудничество с Arasaka и Militech.\\n- NPC: Hanako Arasaka (деловые переговоры), Marko «Fix» Sanchez (коллега/конкурент), Royce (напряжённые переговоры).\\n- Игрок: доверие растёт после `mq-001` и соблюдения уличного кодекса; нарушение слова снижает статус.\\n", "mechanics_links": ["canon/narrative/quests/main/mq-002-choose-path.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Игровые функции и бонусы", "body": "- Narrative-service: выдаёт уличные контракты, координирует ветку `mq-002`, `mq-003`, `mq-201`.\\n- Social-service: открывает социальные перки, снижает штрафы за уличные миссии, модифицирует репутацию Valentinos.\\n- Economy-service: предоставляет независимые каналы поставок и скидки на street-снаряжение.\\n- Навыки: максимальные социальные показатели, средние боевые навыки, низкие технические; уникальная способность отменять уличные рейды.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоговые паттерны", "body": "- Приветствия: «Bienvenido, hijo. Что у тебя за душой?», «Ты доказал, что улицы могут на тебя рассчитывать».\\n- Темы: честь, ответственность, история Heywood, выбор между улицей и корпорациями.\\n- В квестах подчёркивает честь и последствия действий («Это про честь, не про цифры»).\\n", "mechanics_links": [], "assets": []}, {"id": "arc", "title": "Арка развития и финалы", "body": "- Начало: патриарх улиц, поддерживающий порядок.\\n- Развитие: дилемма сотрудничества с корпорациями или сохранения независимости.\\n- Финалы: альянс с игроком и укрепление района, гибель в защите Heywood, уход на покой с передачей наследия.\\n- Quest hooks: «Гроздья Heywood», «Семейное наследие», «Священные граффити».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Street Choice Arc", "link": "canon/narrative/npc-lore/street-choice-index.yaml"}, {"title": "Valentinos Overview", "link": "canon/lore/factions/gangs/valentinos-history.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Padre в YAML и добавление уличных игровых функций."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

