INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-johnny-silverhand', 'Джонни Сильверхенд', '{"metadata": {"id": "canon-npc-johnny-silverhand", "title": "Джонни Сильверхенд", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T23:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "legend", "anti-corporate"], "topics": ["revolution", "night-city"], "related_systems": ["narrative-service", "quest-engine", "reputation-system"], "related_documents": [{"id": "canon-npc-rogue", "relation": "intertwines"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/johnny-silverhand.md", "visibility": "internal", "audience": ["narrative", "encounter-design", "live-ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Легендарный персонаж существовал в Markdown и не имел структурированной связи с сюжетами анти-корпоративной борьбы.", "goal": "Зафиксировать профиль Джонни Сильверхенда в YAML для использования в квестах, событиях и отношениях с игроком.", "essence": "Харизматичный рокер-революционер, продолжающий борьбу против корпораций и вдохновляющий игроков на радикальные решения.", "key_points": ["Символ сопротивления Arasaka и любой корпоративной власти.", "Взаимодействует с игроками через задания, влияющие на репутацию и выбор фракции.", "Сохраняет связи с Rogue, Alt Cunningham и группой Samurai, формируя легендарные сюжетные арки."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Легендарный фронтмен группы Samurai; после 2023 года существует как цифровое сознание.\\n- Появляется в ключевых локациях Night City и в киберпространстве, инициируя сюжетные события.\\n- Используется как катализатор анти-корпоративных квестов и эмоциональных развилок.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Высокий, мускулистый, с металлической левой рукой и рокерской эстетикой.\\n- Импланты боевого и коммуникативного профиля, символические татуировки и аксессуары Samurai.\\n- Визуальный образ опирается на харизму и узнаваемые атрибуты культового персонажа.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и мотивация", "body": "- Происходит из Night City, лидер Samurai, участник атаки на башню Arasaka и жертва корпоративного насилия.\\n- Связи с Alt Cunningham и Rogue формируют эмоциональные узлы сюжетов.\\n- Мотивирован уничтожением корпоративного контроля и поддержкой революционных движений.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и поведение", "body": "- Радикален, эмоционален и харизматичен; использует прямолинейную, бунтарскую речь.\\n- Любимые лозунги: «Wake the fuck up, Samurai!», «We have a city to burn!»\\n- Интересы: музыка, борьба за свободу, вдохновение для сопротивления.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношение к фракциям", "body": "- Враждует с Arasaka и другими корпорациями; поддерживает независимые группы и революционеров.\\n- Друзья: Kerry Eurodyne, Rogue, Alt Cunningham и другие участники Samurai.\\n- Отношения с игроком зависят от анти-корпоративных выборов и готовности к риску.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Квестовые линии: «Пробуждение легенды», «Борьба с Arasaka», «Революция».\\n- Предлагает информацию о истории Night City, доступ к сопротивлению и уникальные награды.\\n- Навыки: высокий социальный и боевой потенциал, средние хакерские и технические показатели.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Базовые диалоги NPC", "link": "../../dialogues/npc-dialogues-basic.md"}, {"title": "План основных квестов", "link": "../../quests/main-quests-outline.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Джонни Сильверхенда в YAML и подготовка анти-корпоративных сюжетных связок."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

