INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-judy-alvarez', 'Джуди Альварес', '{"metadata": {"id": "canon-npc-judy-alvarez", "title": "Джуди Альварес", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T22:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "mox", "braindance"], "topics": ["characters", "justice"], "related_systems": ["narrative-service", "quest-engine", "social-service"], "related_documents": [{"id": "canon-faction-mox", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/judy-alvarez.md", "visibility": "internal", "audience": ["narrative", "social-design", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Джуди Альварес требовал перевода в структурированный формат и увязки с сюжетами Мокси и брейндансов.", "goal": "Описать характер, мотивацию и игровые взаимодействия техники брейндансов для линий справедливости и социального прогресса.", "essence": "«Эмпатичная нетраннерка Мокси, использующая технологии против эксплуатации».", "key_points": ["Защищает секс-работников и борется с корпоративной жестокостью, задавая моральную линию Мокси.", "Доступ к брейндансам и хакерским заданиям делает её проводником через социальные и техно-арки.", "Отношения с игроком зависят от выбора в пользу справедливости и поддержки слабых."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Техник по брейндансам, нетраннер и активистка банды Мокси.\\n- Рабочая база — Lizzie''s Bar в районе Watson.\\n- Через неё запускаются сюжетные ветки защиты уязвимых групп.\\n", "mechanics_links": ["shared/docs/knowledge/canon/narrative/scenarios/factions/gangs/gangs-2020-2093.md"], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Розовые волосы, татуировки Мокси, выразительный киберпанк-стиль.\\n- Импланты: связи, хакерские модули, специализированные интерфейсы для брейнданса.\\n- Одежда практична, сочетает защиту и уличную эстетику.\\n", "mechanics_links": [], "assets": []}, {"id": "motivations", "title": "Мотивация и характер", "body": "- Ценности: справедливость, защита уязвимых, этичное использование технологий.\\n- Страхи: эксплуатация людей, утрата друзей, злоупотребление брейндансами.\\n- Эмоциональный профиль: проницательность, эмпатия, готовность к протесту.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношения", "body": "- Дружит с членами Мокси и Evelyn Parker, противостоит корпорациям и хищным бандам.\\n- Нейтрально относится к независимым группам, пока они не угрожают слабым.\\n- Игроку доверяет при поддержке справедливости; возможна романтическая линия.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Квестовые цепочки: брейндансы, хакерство, защита Мокси, социальные реформы.\\n- Предоставляет услуги: обработка брейндансов, взлом инфраструктуры, эмоциональная поддержка.\\n- Навыки: высокие хакерские и технические способности, низкая боевая подготовка.\\n", "mechanics_links": ["mechanics/social/player-orders-via-npc.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "План основных квестов", "link": "shared/docs/knowledge/canon/narrative/quests/main-quests-outline.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Джуди Альварес в YAML и определение геймплейных связей."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

