INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-rogue', 'Rogue Amendiares', '{"metadata": {"id": "canon-npc-rogue", "title": "Rogue Amendiares", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T23:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "fixer", "afterlife"], "topics": ["night-city", "underworld"], "related_systems": ["narrative-service", "quest-engine", "reputation-system"], "related_documents": [{"id": "canon-npc-johnny-silverhand", "relation": "intertwines"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/rogue.md", "visibility": "internal", "audience": ["narrative", "encounter-design", "worldbuilding"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Rogue существовал в Markdown и не был структурирован для поддержки сюжетов бара Afterlife и репутационных систем.", "goal": "Сформировать каноническое описание Rogue как центрального фиксера Night City для интеграции в квестовые и социальные сценарии.", "essence": "Харизматичный и независимый фиксер, управляющий Afterlife и задающий тон высокорисковым контрактам.", "key_points": ["Rogue распределяет задания высокого риска и требует от игроков подтверждённой репутации.", "Связи персонажа охватывают легендарных наёмников и бывшие отношения с Джонни Сильверхендом.", "Балансирует нейтралитет между фракциями, торгуя информацией и сохраняя независимость."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Имя: Rogue Amendiares; легендарный фиксер Night City и владелица бара Afterlife.\\n- Работает как независимый оператор, выстраивает сеть контактов среди наёмников и корпораций.\\n- Используется для вывода игроков на контракты самой высокой сложности и статуса.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Возраст 50–55, уверенная осанка, ухоженный образ с минимальными визуальными модификациями.\\n- Киберимпланты связи, боевые и стелс-модули скрыты, подчёркивая выбор естественного стиля.\\n- Палитра тёмных тонов и фирменные аксессуары подчёркивают статус и профессионализм.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и мотивация", "body": "- Происходит с улиц Night City, выстроила карьеру через сеть наёмников и сотрудничество с легендами.\\n- Управляет Afterlife, превращая бар в центр контрактов и символ статуса.\\n- Мотивирована поддержанием репутации, независимости и контролем над распределением опасных миссий.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и поведение", "body": "- Харизматичная, строгая, прагматичная; работает только с проверенными исполнителями.\\n- Использует формальную, уверенную манеру речи, подчёркивая цену репутации и риск миссий.\\n- Интересы сосредоточены на управлении сетью наёмников и сохранении влияния Afterlife.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношение к фракциям", "body": "- Поддерживает нейтральные рабочие отношения с Arasaka, Militech и городскими бандами при соблюдении условий сделки.\\n- Дружеские связи с Джонни Сильверхендом и другими легендарными наёмниками формируют эксклюзивное окружение.\\n- С игроками строит отношения через выполненные задания и высокий рейтинг доверия.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Предоставляет цепочки «Легендарные задания», «Опасные миссии» и «Репутация», требующие высокого уровня игрока.\\n- Сервисы: доступ к элитным контрактам, обмен информацией, посредничество между фракциями.\\n- Навыки: высокий социальный и боевой опыт, средние технические навыки, акцент на управлении репутацией.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Список важных NPC", "link": "../important-npcs-list.md"}, {"title": "Базовые диалоги NPC", "link": "../../dialogues/npc-dialogues-basic.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Rogue из Markdown и структурирование игровых функций фиксера."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

