INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-kurt-hansen', 'Kurt Hansen — лидер Dogtown', '{"metadata": {"id": "canon-npc-kurt-hansen", "title": "Kurt Hansen — лидер Dogtown", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "dogtown", "military"], "topics": ["narrative", "conflict"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-scenarios-locations-night-city-dogtown-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/kurt-hansen.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль лидера Dogtown требовал переноса в YAML.", "goal": "Зафиксировать военизированного командира, управляющего частной армией и территорией Dogtown.", "essence": "Kurt Hansen обеспечивает контроль силой, используя контракты на штурмы и диверсии против NUSA и корпораций.", "key_points": ["Поддерживает военный режим и безопасность через наёмников.", "Предлагает задания на проникновение, диверсии и удержание объектов.", "Конфликтует с правительством NUSA и корпоративными интересами."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Kurt Hansen руководит Dogtown как частной армией, управляя экономикой и безопасностью района. Он использует строгую дисциплину\\nи силовые методы, чтобы удерживать власть и монополизировать ресурсы.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Контракты включают инфильтрации, диверсии и прямые конфликты. Игроки сталкиваются с усиленной охраной и необходимостью работать\\nпротив NUSA или корпораций.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"Порядок стоит дорого. Я умею считать.\\" — реплика подчёркивает его прагматизм и приоритет экономической выгоды.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Dogtown 2020-2093", "link": "../../../../scenarios/locations/night-city/dogtown-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-05", "author": "npc_team", "changes": "Создан профиль Kurt Hansen."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

