INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-saburo-arasaka', 'Сабуро Арасака — архитектор корпоративной власти', '{"metadata": {"id": "npc-saburo-arasaka", "title": "Сабуро Арасака — архитектор корпоративной власти", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.2.0", "last_updated": "2025-11-12T01:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "arasaka", "corporate-history"], "topics": ["npc-lore", "corporate-politics"], "related_systems": ["narrative-service", "world-service"], "related_documents": [{"id": "npc-hanako-arasaka", "relation": "ancestor_of"}, {"id": "npc-yorinobu-arasaka", "relation": "ancestor_of"}, {"id": "canon-lore-corporate-politics-master", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/saburo-arasaka.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сабуро Арасака был описан кратко в Markdown и не связывался с корпоративными арками и историческими событиями.", "goal": "Структурировать профиль основателя Arasaka для использования в сюжетных флэшбеках, корпоративных миссиях и справочниках.", "essence": "«Легендарный стратег, создавший корпоративную империю и оставивший наследие, которым пытаются управлять наследники.»", "key_points": ["Зафиксированы функции Сабуро как ориентир лора и мотиватор конфликтов наследников.", "Добавлены мотивации, страхи и исторические вехи для использования в кат-сценах и учебных материалах.", "Определены хуки для world-service и корпоративных квестов."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и позиционирование", "body": "- Полное имя: Saburo Arasaka, ID `npc-saburo-arasaka`.\\n- Роль: глава и идеолог корпорации Arasaka, символ корпоративного доминирования.\\n- Локации: Токио, Найт-Сити (исторические владения), закрытые корпоративные комплексы.\\n- Статус: легендарная фигура, чьё наследие продолжает влиять на сюжет после гибели.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и наследие", "body": "- Основная мотивация: абсолютный контроль и расширение влияния Arasaka.\\n- Цели: консолидация власти корпорации, сохранение династии, устранение угроз Найт-Сити.\\n- Страхи: потеря наследия, вмешательство конкурентов, слабость наследников.\\n- Наследие формирует давление на Hanako и Yorinobu, служит ориентиром для корпоративных фанатиков.\\n", "mechanics_links": [], "assets": []}, {"id": "biography", "title": "История и ключевые события", "body": "- Построил Arasaka как глобальный военный и финансовый конгломерат.\\n- Организовал экспансию в Найт-Сити и утвердил корпоративный кодекс контроля.\\n- Ключевые события: корпоративные войны, создание сети «Arasaka Security», кибероперации против конкурентов.\\n- После гибели его тень управляет решениями наследников и запускает арки памяти/Relic.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и поведение", "body": "- Черты: хладнокровный стратег, дисциплинированный, авторитарный.\\n- Манера речи: размеренная, властная; цитаты «Власть принадлежит тем, кто готов её удержать».\\n- Интересы: военная доктрина, корпоративная философия, контроль над информацией.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и влияние", "body": "- Семья: Hanako и Yorinobu — ключевые наследники, конфликтующие из-за трактовки наследия.\\n- Корпорации: вечный конкурент Militech, непримиримый противник NUSA и коррумпированных фракций.\\n- Игрок: появляется в архивных материалах, кат-сценах и миссиях Relic в роли антагониста-символа.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/corporations/arasaka-2020-2093.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Игровое значение", "body": "- Narrative-service: флэшбеки, архивные миссии, кат-сцены обучения наследников.\\n- World-service: события корпоративной пропаганды и «День наследия Arasaka».\\n- Используется как моральный ориентир в ветке реформ Йоринобу и дипломатии Hanako.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Реплики и легенды", "body": "- Легенды улиц цитируют Сабуро в миссиях Arasaka, создавая атмосферу культа личности.\\n- Пример: «Власть принадлежит тем, кто готов её удержать» — используется в обучающих записях и брифингах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Era Index — Arasaka", "link": "canon/narrative/npc-lore/era-index.md"}, {"title": "Timeline 2077 Events", "link": "canon/lore/_03-lore/timeline/2077-game-events.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Сабуро Арасаки в структурированный YAML и дополнение игровыми связями."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

