INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-meredith-stout', 'Meredith Stout', '{"metadata": {"id": "canon-npc-meredith-stout", "title": "Meredith Stout", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T23:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "militech", "operative"], "topics": ["corporate-warfare", "covert-ops"], "related_systems": ["narrative-service", "quest-engine", "reputation-system"], "related_documents": [{"id": "canon-npc-hiroshi-tanaka", "relation": "rivals"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/meredith-stout.md", "visibility": "internal", "audience": ["narrative", "encounter-design", "combat-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль оперативника Militech находился в Markdown и не покрывал потребности сценариев силовых контрактов.", "goal": "Перевести описание Meredith Stout в структурированный формат для использования в корпоративных и боевых квестах.", "essence": "Жёсткий куратор Militech, предлагающий рискованные операции с высокими ставками и минимальной терпимостью к провалам.", "key_points": ["Представляет агрессивную стратегию Militech и использует исполнителей как инструмент.", "Предлагает высокооплачиваемые задания с жёсткими дедлайнами и репутационными последствиями.", "Вступает в прямой конфликт с Arasaka и криминальными структурами Night City."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Оперативник Militech, руководит силовыми и диверсионными задачами корпорации.\\n- База операций — штаб Militech в Downtown; встречается с агентами в защищённых офисах.\\n- Обеспечивает игрокам доступ к «грязной работе» корпорации с повышенными выплатами.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и оснащение", "body": "- Возраст 30–40 лет, холодный взгляд, строгая тактическая экипировка.\\n- Предпочитает бронированные костюмы, подчеркивающие статус и готовность к бою.\\n- Использует стандарт Militech по оружию и имплантам защиты.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и мотивация", "body": "- Построила карьеру на успешных спецоперациях и безжалостности к противникам.\\n- Репутация связана с жёсткими решениями и высоким уровнем риска.\\n- Мотивирована доминированием Militech и устранением корпораций-конкурентов.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и противостояния", "body": "- Конфликтует с Arasaka, рассматривая их как главного соперника.\\n- Взаимодействует с бандами и независимыми силами сугубо прагматично и под NDA.\\n- С игроками строит деловые отношения, оценивая только результат и дисциплину.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Контракты: штурм, диверсии, ликвидации, сопровождение и силовые рейды.\\n- Награды включают кредиты, военное оборудование и повышение статуса в Militech.\\n- Риск: ухудшение отношений с бандами и корпорациями, нарушения приводят к санкциям.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Тон и коммуникация", "body": "- Речь короткая и приказная, без терпимости к оправданиям.\\n- Ключевые реплики: «Результат. Без оправданий.»; «Либо ты в деле, либо ты помеха.»\\n- Используется в сценах, подчеркивающих давление времени и жесткость корпорации.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Список важных NPC", "link": "../important-npcs-list.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Meredith Stout в YAML и определение силовых сюжетных функций."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

