INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-james-iron-reed', 'James "Iron" Reed — генерал Militech', '{"metadata": {"id": "canon-npc-james-iron-reed", "title": "James \\"Iron\\" Reed — генерал Militech", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-07T22:34:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "militech", "corporate-war"], "topics": ["narrative", "warfare"], "related_systems": ["npc-service", "world-service", "social-service"], "related_documents": [{"id": "canon-npc-hanako-arasaka", "relation": "rivals"}, {"id": "canon-npc-padre-2040-2085", "relation": "negotiates"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/militech-james-reed.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Corporate Narrative Cell", "reviewed_at": "2025-11-07T22:34:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Детализированный профиль генерала Militech существовал в Markdown и требовал переноса в структурированную базу знаний.", "goal": "Зафиксировать историю, мотивации и механику взаимодействий Джеймса «Iron» Рида в корпоративных конфликтах Night City.", "essence": "Рид — стратег Militech, ведущий войну против Arasaka, контролирующий спецоперации и влияющий на корпоративные ветки.", "key_points": ["Руководит оперативным штабом Militech в Night City и организует теневые операции.", "Стремится сломить Arasaka, поддерживает дисциплину и политическое влияние.", "Влияет на корпоративные и боевые миссии, обеспечивая поддержку дронами и войсками."]}, "content": {"sections": [{"id": "appearance", "title": "Внешний вид", "body": "Атлетичный мужчина около пятидесяти лет с титанизированными имплантами рук, узнаваемым шрамом на правой щеке и военной униформой\\nс цифровым камуфляжем.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и биография", "body": "Выпускник академии Militech, участник корпоративных войн, Рид командовал операцией «Iron Shield» против Maelstrom, перехватывал\\nпоставки Arasaka и продвигает программу внедрения боевых имплантов в Night City.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивации и цели", "body": "Главная цель — укрепить Militech и сломить влияние Arasaka. Он стремится контролировать стратегические объекты, поглощать\\nнезависимые контракты и нейтрализовать уличные угрозы, опасаясь утечки информации и потери поддержки.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и манеры", "body": "Жёсткий дисциплинированный стратег, говорящий краткими командами. Ценит силу и точность, уважает боеспособных союзников и\\nне терпит предателей.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения", "body": "Поддерживает абсолютную лояльность Militech, ведёт скрытую войну с Arasaka, использует Valentinos для разведки и взаимодействует\\nс Padre ради мира в Heywood. Для игрока доступен как источник контрактов Militech или как антагонист в других ветках.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая механика", "body": "Предоставляет миссии Militech, влияет на корпоративные истории (`mq-101`, `corp-war`), выступает финальным противником в\\nнекоторых концовках и может вызвать поддержку дронов и бронетехники.\\n", "mechanics_links": [], "assets": []}, {"id": "inventory", "title": "Навыки и инвентарь", "body": "Эксперт ближнего и дальнего боя, вооружён штурмовой винтовкой \\"Iron Storm\\", пистолетом \\"Militech Defender\\", гранатами, носит\\nусиленный тактический костюм и использует разведывательный дрон.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "«Militech не блефует. Мы действуем.»\\n«Если хочешь мира — подготовься к войне.»\\n«Синтетика должна служить человеку, а не наоборот.»\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Развитие персонажа", "body": "Возможные арки включают победу над Arasaka при поддержке игрока, поражение из-за предательства или временный союз против общей\\nугрозы. Его решения зависят от репутации игрока среди корпораций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Militech Countermesh Ops", "link": "../../quests/corporate/militech-ops-index.yaml"}, {"title": "Архи-конфликты Arasaka vs Militech", "link": "../../conflicts/arasaka-militech-overview.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-07", "author": "npc_team", "changes": "Создан профиль генерала Militech."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

