INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-misty', 'Мисти Олшевски — хранительница человечности', '{"metadata": {"id": "npc-misty", "title": "Мисти Олшевски — хранительница человечности", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-02T18:50:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "mystic", "social"], "topics": ["npc-biographies", "humanity-systems"], "related_systems": ["social-service", "gameplay-service", "narrative-service", "character-service"], "related_documents": [{"id": "canon-narrative/stories/cyberpunk-inspiration-stories", "relation": "references"}, {"id": "mechanics/world/events/live-events-system", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/misty.md", "visibility": "internal", "audience": ["concept", "narrative", "ux"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Мисти оставался в Markdown и не связывался с системами человечности и социального взаимодействия.", "goal": "Структурировать данные о таро-читательнице для сюжетов Heywood и механик поддержки игроков.", "essence": "Заботливая мистик, поддерживающая людей через таро и напоминая о ценности человечности в Night City.", "key_points": ["Владелица Misty''s Esoterica, живёт рядом с клиникой Виктора и поддерживает Джеки.", "Помогает игрокам через таро, советы и события, поддерживающие мораль и психическое здоровье.", "Связана с гуманистическими сюжетами и Live Ops, где требуется социальная поддержка.", "Представляет канал к нетрадиционным предметам и метаинформации о судьбе."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Мисти Олшевски (ID `npc-misty`) — таро-читательница и владелица магазина Misty''s Esoterica в Heywood.\\nСветловолосая, с мистическими аксессуарами и спокойным взглядом. Поддерживает атмосферу уюта и человечности\\nвозле клиники Виктора.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и ценности", "body": "Выросла на улицах Heywood, рано увлеклась мистикой и открыла собственный магазин. Ценит независимость, заботу\\nо друзьях и сохранение человечности. Боится потерять близких и видеть, как Night City ломает людей.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "services", "title": "Услуги и механики", "body": "Предлагает чтение таро, кристаллы, советы по устойчивости, участвует в заданиях «Таро», «Мистика», «Человечность».\\nПовышает мораль отрядов, даёт временные бафы против киберпсихоза, показывает скрытые аспекты сюжетов.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "relationships", "title": "Связи", "body": "Романтические отношения с Джеки Уэллсом, дружит с Виктором Вектором и другими гуманистами. С фракциями держит\\nдистанцию, помогая всем, кто ищет поддержку.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоговый тон", "body": "Реплики: «Карты говорят о многом.», «Важно сохранить человечность.», «Мы должны поддерживать друг друга.»\\nМанера — тихая, ободряющая, с оттенком мистики. При высокой репутации делится более глубокими видениями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 17, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-npc-misty"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован профиль Мисти и связан с системами человечности и социальными событиями."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

