-- NPC: canon-npc-mystic-misty-olshevski-2020-2093
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-mystic-misty-olshevski-2020-2093', 'Misty Olshevski — хранительница человечности и мистик', '{"metadata": {"id": "canon-npc-mystic-misty-olshevski-2020-2093", "title": "Misty Olshevski — хранительница человечности и мистик", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-20T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-20T00:00:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "mystic", "nomad", "aldcalderas", "fixer", "spirituality", "tarot"], "topics": ["narrative", "quests", "factions", "mysticism"], "related_systems": ["npc-service", "social-service", "world-service"], "related_documents": [{"id": "canon-npc-fixer-marco-fix-sanchez", "relation": "complements"}, {"id": "canon-scenarios-locations-badlands-2020-2093", "relation": "references"}, {"id": "canon-factions-nomads-aldcalderas-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/misty-olshevski-detailed-2020-2093.md", "visibility": "internal", "audience": ["narrative", "gameplay", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Street Narrative Cell", "reviewed_at": "2025-12-20T00:00:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"title": "Misty Olshevski — хранительница человечности", "description": "Misty Olshevski — мистик и целительница из клана Aldecaldos, живущая в Badlands. Она сочетает древние традиции с современными технологиями,\\nпредлагая духовное руководство и лечение в мире, где человечность под угрозой.\\n\\nЕе клиника в Badlands служит убежищем для тех, кто ищет исцеления не только тела, но и души. Misty верит, что технологии могут усилить\\nдуховные способности человека, а не заменить их.\\n", "key_characteristics": ["Мистик и целительница", "Член клана Aldecaldos", "Практикует таро и спиритизм", "Верит в баланс технологий и человечности", "Помогает героям найти внутренний покой"], "locations": [{"primary": "Misty''s Esoterica (Badlands clinic)"}, {"secondary": "Aldecaldos camps, Night City (occasional visits)"}], "relationships": [{"panam_palmer": "Close friend and spiritual guide"}, {"jackie_welles": "Occasional spiritual consultation"}, {"claire": "Business acquaintance in Night City"}], "age": 28, "gender": "female", "faction": "Aldecaldos", "occupation": "Mystic healer and spiritual guide"}, "content": [{"id": "background", "title": "Происхождение и ранняя жизнь", "body": "Misty выросла в клане Aldecaldos в Badlands. Ее мать была шаманкой, а отец - техником, что сформировало ее уникальный взгляд на мир.\\nВ юности она пережила тяжелую утрату близкого человека, что привело ее к изучению спиритизма и поиску смысла жизни.\\n\\nОна открыла свою клинику \\"Misty''s Esoterica\\" в 2048 году, где сочетает традиционные методы лечения с современными имплантами.\\nЕе подход заключается в том, что технологии должны служить человечности, а не заменять ее.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и мировоззрение", "body": "Misty — спокойная, мудрая женщина с глубоким пониманием человеческой природы. Она не судит других, но всегда говорит правду,\\nдаже если она неприятна. Ее голос успокаивает, а присутствие приносит мир.\\n\\nОна верит, что каждый человек несет в себе искру божественного, и задача мистика — помочь этой искре разгореться.\\nMisty часто цитирует древние тексты, адаптируя их к реалиям 2077 года.\\n\\n\\"Технологии — это инструменты. Они могут усилить вашу душу или похоронить ее. Выбор за вами.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности и навыки", "body": "**Мистические способности:**\\n- Таро-гадалка с поразительной точностью предсказаний\\n- Способность чувствовать \\"ауру\\" людей и машин\\n- Лечение через медитацию и ритуалы\\n- Создание защитных амулетов и талисманов\\n\\n**Технические навыки:**\\n- Специалист по биоимплантам\\n- Знание кибернетических систем\\n- Создание гибридных устройств (технология + магия)\\n\\n**Социальные навыки:**\\n- Посредник в конфликтах\\n- Духовный наставник\\n- Советчик для героев в поисках смысла жизни\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения с другими персонажами", "body": "**Panam Palmer:** Близкая подруга и духовная сестра. Misty часто выступает как духовный гид для Panam,\\nпомогая ей справиться с травмами прошлого.\\n\\n**Jackie Welles:** Jackie иногда приходит к Misty за духовными консультациями. Она уважает его кодекс чести,\\nно беспокоится о его наивности.\\n\\n**Vasco:** Как член Aldecaldos, Vasco относится к Misty с уважением, но не всегда понимает ее методы.\\n\\n**Claire:** Misty иногда посещает Afterlife в Night City и обменивается историями с Claire.\\n", "mechanics_links": [], "assets": []}, {"id": "role_in_story", "title": "Роль в сюжете", "body": "Misty служит моральным компасом в жестоком мире Night City. Она представляет альтернативу - путь баланса между\\nтехнологиями и человечностью. Ее клиника становится убежищем для героев, ищущих не только физического,\\nно и духовного исцеления.\\n\\nВ ключевых моментах сюжета Misty может:\\n- Предоставить важную информацию через таро\\n- Помочь герою принять трудное решение\\n- Исцелить не только тело, но и душу персонажа\\n- Открыть новые перспективы на мир игры\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Развитие персонажа", "body": "Misty может развиваться в зависимости от выборов игрока:\\n\\n**Путь духовного лидера:** Misty становится духовным лидером Aldecaldos, помогая клану найти баланс\\nмежду традициями и современностью.\\n\\n**Путь целительницы:** Misty расширяет свою клинику, становясь центром исцеления для всего Badlands.\\n\\n**Путь мистика:** Misty углубляется в изучение древних знаний, становясь настоящим духовным авторитетом.\\n\\nВозможна трагическая арка, если игрок предает доверие Misty - она может отвернуться от мира,\\nстав циничной и закрытой.\\n", "mechanics_links": [], "assets": []}, {"id": "inspiration", "title": "Источники вдохновения", "body": "Misty вдохновлена образами шаманов и мистиков из различных культур, адаптированных к киберпанковскому миру.\\nЕе философия сочетает идеи буддизма, шаманизма и современных духовных практик.\\n\\nВдохновение также черпается из историй о людях, которые сохранили человечность в самых жестоких условиях.\\nМузыкальные предпочтения: ambient и этническая музыка, помогающая медитации.\\n", "mechanics_links": [], "assets": []}], "appendix": {"glossary": [], "references": [{"title": "Jackie Welles — профиль", "link": "jackie-welles-detailed-2020-2093.yaml"}, {"title": "Panam Palmer — профиль", "link": "panam-palmer.yaml"}, {"title": "Aldecaldos — фракция", "link": "../../../../factions/nomads/aldcalderas-2020-2093.yaml"}, {"title": "Badlands 2020-2093", "link": "../../../../scenarios/locations/badlands-2020-2093.yaml"}], "decisions": []}, "implementation": {"github_issue": 140875182, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-20"}]}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

