INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-netwatch-agent-vogel', 'Агент NetWatch «Vogel»', '{"metadata": {"id": "canon-npc-netwatch-agent-vogel", "title": "Агент NetWatch «Vogel»", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.1.0", "last_updated": "2025-11-11T23:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "netwatch", "netrunner"], "topics": ["cybersecurity", "blackwall"], "related_systems": ["narrative-service", "quest-engine", "netrunner-systems"], "related_documents": [{"id": "canon-technology-blackwall-2023-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/netwatch-agent-vogel.md", "visibility": "internal", "audience": ["narrative", "systems-design", "encounter-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Досье агента NetWatch оставалось в Markdown и не связывалось с механиками Blackwall и квестами борьбы с киберугрозами.", "goal": "Структурировать профиль Vogel для использования в сюжетах NetWatch, моральных дилеммах и заданиях нетраннеров.", "essence": "Холодный профессионал NetWatch, охраняющий Blackwall и использующий игроков для нейтрализации нелегальных нетраннеров.", "key_points": ["Курирует операции против Voodoo Boys и отслеживает попытки прорваться за Blackwall.", "Даёт квесты, ставящие вопрос баланса между безопасностью и свободой Net.", "Обладает доступом к эксклюзивным программам NetWatch и может временно защищать игроков."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Полное имя: Bryce Mosley, кодовое имя «Vogel»; элитный агент NetWatch.\\n- Локации: центральные узлы City Center, мобильные точки подключения и виртуальные зоны Net.\\n- Ответственность: координация операций против нелегальных нетраннеров и защита критических сегментов Net.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Мужчина 40–50 лет, строгие черты, деловые костюмы с скрытыми защитными элементами.\\n- Импланты: продвинутые нейроинтерфейсы NetWatch, защита от ICE, биомониторы, анализаторы трафика.\\n- Сдержанный стиль с тонкими акцентами символики NetWatch.\\n", "mechanics_links": [], "assets": []}, {"id": "background", "title": "История и мотивация", "body": "- Ранние способности к нетраннингу привели к вербовке NetWatch и интенсивной подготовке.\\n- Участвовал в операциях против Voodoo Boys, расследованиях кибератак и защите инфраструктуры.\\n- Мотивирован предотвращением прорыва Blackwall и поддержанием контроля над Net.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и дилеммы", "body": "- Флегматичен, прагматичен, скрывает усталость от бесконечных угроз.\\n- Манера речи техническая и точная, часто подчёркивает риски и протоколы.\\n- Внутренне балансирует между долгом и пониманием цены тотального контроля.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и фракции", "body": "- Абсолютно лоялен NetWatch, работает в тесной связке с городскими службами безопасности.\\n- Непримирим с Voodoo Boys и нелегальными нетраннерами; сотрудничает с корпорациями лишь при необходимости.\\n- С игроками поддерживает осторожное сотрудничество, оценивая их действия в Net.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Квестовые ветки: «За Blackwall», «Охота на призраков», «Цена порядка».\\n- Награды: доступ к программам NetWatch, снижение преследования, эксклюзивные intel-пакеты.\\n- Навыки: очень высокие нетраннерские способности, средние боевые, высокий технический анализ.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Эпохальный индекс NPC", "link": "../era-index.md"}, {"title": "Таймлайн 2060-2077", "link": "../../_03-lore/timeline/2060-2077-pre-modern.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-05", "author": "narrative_team", "changes": "Первичное описание агента NetWatch в Markdown."}, {"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Vogel в YAML и структурирование квестовых функций NetWatch."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

