INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-mr-hands', 'Mr. Hands — архитектор Pacifica', '{"metadata": {"id": "npc-mr-hands", "title": "Mr. Hands — архитектор Pacifica", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-12T01:35:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-12T01:35:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "fixer", "pacifica"], "topics": ["npc-lore", "contract-design"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "canon-narrative/factions/gangs/voodoo-boys-profile", "relation": "negotiates"}, {"id": "canon-lore/regions/pacifica-detailed.yaml", "relation": "references"}, {"id": "canon-narrative/npc-lore/important/placide.yaml", "relation": "conflicts"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/mr-hands.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Mr. Hands был в Markdown и не фиксировал системные сценарии Pacifica.", "goal": "Перевести фикcера высшего уровня в YAML 1.1.0, описав его сеть контрактов и влияние на эндгейм-контент.", "essence": "Серый кардинал Pacifica, управляющий хаосом района через сеть контрактов и стратегические альянсы.", "key_points": ["Зафиксированы биография, мотивация и долгосрочные цели фикcера.", "Добавлены связи с корпорациями, Voodoo Boys и независимыми операторами.", "Определены крючки Narrative/Social/Economy для контрактов высокой сложности."]}, "content": {"sections": [{"id": "identity", "title": "Позиционирование", "body": "Mr. Hands — элитный фиксер района Pacifica, контролирующий высокорисковые операции и сеть посредников. ID `npc-mr-hands`. Работает из скрытых офисов, контактирует с игроками через доверенных курьеров и защищённые каналы.\\n", "mechanics_links": [], "assets": []}, {"id": "biography", "title": "История и рост", "body": "Начал как уличный брокер, пережил крах проекта Pacifica и захватил теневой рынок района. Построил разветвлённую сеть информаторов, заключил сделки с корпорациями и кочевыми кланами. Несколько раз становился целью устранения, но укрепил позиции благодаря дальновидности.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Мотивация — контроль и долгосрочная стабильность Pacifica. Цели: превратить район в независимую зону влияния, уменьшить зависимость от корпораций, подготовить преемника. Страхи: хаос без контроля, предательство союзников, силовое вторжение Militech.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и баланс сил", "body": "Фракции: Arasaka и Militech — прагматичные сделки, Voodoo Boys — напряжённое сотрудничество, банды Pacifica — удерживает дисциплиной. Игроки получают доступ к контрактам только после успешного прохождения испытательных миссий.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/gangs/voodoo-boys.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Системные крючки", "body": "Narrative-service: цепочки «Тени Pacifica», «Игра в долгую», «Наследие района». Social-service: проверка репутации, дипломатия с фракциями, выбор преемника. Economy-service: доступ к эксклюзивному снаряжению, апгрейд инфраструктуры Pacifica, управление налогами с контрактов.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоговый профиль", "body": "Речь сдержанная и прагматичная: «Время — валюта. Не трать мою», «В Pacifica правила пишут те, кто выживает». Реагирует на прогресс игрока доступом к более сложным задачам. При провалах связь замораживается до компенсации ущерба.\\n", "mechanics_links": [], "assets": []}, {"id": "arcs", "title": "Арки и финалы", "body": "Арка «Независимая Pacifica»: игрок помогает Mr. Hands укрепить контроль над районом или поддерживает корпорации. Финалы включают независимость Pacifica, изгнание фикcера или передачу власти игроку. Связанные квесты — `pacifica-long-game`, `corporate-shadows`, `successor-trial`.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Pacifica District Overview", "link": "canon/lore/regions/pacifica-detailed.yaml"}, {"title": "Voodoo Boys Operations", "link": "canon/narrative/factions/gangs/voodoo-boys-profile.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Mr. Hands в YAML с добавлением системных цепочек Pacifica."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

