INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-tyger-claws-enforcer', 'Боевик Tyger Claws (Enforcer)', '{"metadata": {"id": "npc-common-enemy-tyger-claws-enforcer", "title": "Боевик Tyger Claws (Enforcer)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "tyger-claws", "enforcer"], "topics": ["gang_conflicts", "combat_encounters"], "related_systems": ["combat-service", "narrative-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/tyger-claws-enforcer.md", "visibility": "internal", "audience": ["concept", "narrative", "combat"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен формализованный профиль боевика Tyger Claws для боевых и сюжетных сценариев.", "goal": "Описать стиль боя, способности и точки появления гангстера в Japantown.", "essence": "Скоростной боец ближнего боя с клинковым оружием и оптическими модами, опасный в клубных кварталах.", "key_points": ["Использует рывки и кровотечения для давления на игрока.", "Слаб к оглушению и контролю импульсным оружием.", "Охраняет клубы и точки влияния Tyger Claws в Japantown."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Боец клана Tyger Claws, сочетает традиционные клинки с кибернетикой.\\nРаботает в парах или малых отрядах, патрулирующих клубы и нелегальные арены Japantown.\\n", "mechanics_links": ["mechanics/combat/combat-implants-overview.yaml"], "assets": []}, {"id": "combat_style", "title": "Боевое поведение", "body": "- Стремительные рывки к цели, серия ударов клинками.\\n- Наносит кровотечения и использует оптические ослепления.\\n- Уязвим к контратакам во время восстановления после рывка.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "encounter_hooks", "title": "Сценарии встреч", "body": "- Нападения на игроков, вмешивающихся в бизнес Tyger Claws.\\n- Охрана VIP-персон и рекет клубов.\\n- Квесты на внедрение или устранение ключевых бойцов банды.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "loot", "title": "Трофеи", "body": "- Модифицированные клинки и импланты ближнего боя.\\n- Токены доступа к клубам и подпольным аренам.\\n- Информация о теневых сделках Tyger Claws.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль боевика Tyger Claws в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

