INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-panam-palmer', 'Панам Палмер', '{"metadata": {"id": "canon-npc-panam-palmer", "title": "Панам Палмер", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-11T22:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "nomad", "aldacaldos"], "topics": ["characters", "freedom"], "related_systems": ["narrative-service", "quest-engine", "world-service"], "related_documents": [{"id": "canon-narrative-scenarios-faction-index", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/panam-palmer.md", "visibility": "internal", "audience": ["narrative", "encounter-design", "worldbuilding"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Панам Палмер требовал структурирования для поддержки квестов пустошей и отношений с кланом Альдекальдо.", "goal": "Сформировать лор, мотивацию и игровые функции кочевницы, обеспечив связность сюжетов свободы и боевых операций.", "essence": "«Свободолюбивая наёмница Альдекальдо, готовая рисковать ради семьи и независимости».", "key_points": ["Представляет номадов и storyline борьбы за автономию вне корпораций.", "Специализируется на боевых и транспортных операциях в пустошах.", "Отношения с игроком зависят от поддержки клана и выбора свободы вместо корпоративных контрактов."]}, "content": {"sections": [{"id": "identity", "title": "Профиль и роль", "body": "- Бывшая член клана Альдекальдо, превратившая опыт в работу независимого наёмника.\\n- Локации: пустоши за пределами Night City, импровизированные базы клана.\\n- Задействуется в сюжетах о спасении караванов и защите территории.\\n", "mechanics_links": ["shared/docs/knowledge/canon/narrative/scenarios/factions/faction-scenarios-index.yaml"], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Темные волосы, татуировки клана, практичная боевая экипировка.\\n- Импланты: коммуникации, вождение, бой; визуальных модификаций минимум.\\n- Цветовая палитра: тёплые земляные тона, кожа и металл.\\n", "mechanics_links": [], "assets": []}, {"id": "motivations", "title": "Мотивация и характер", "body": "- Ценности: свобода, верность семье, готовность защищать союзников.\\n- Страхи: потерять независимость или дезертировать от друзей.\\n- Поведение: прямолинейная, решительная, действует быстро.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношение к фракциям", "body": "- Дружит с Альдекальдо, поддерживает номадские кланы и независимых наёмников.\\n- Враждует с корпорациями и врагами клана; нейтральна к бандам, если те не угрожают свободе.\\n- С игроком строит доверие через помощь клану и отказ от корпоративных сделок.\\n", "mechanics_links": ["mechanics/social/mentorship-system.yaml"], "assets": []}, {"id": "gameplay", "title": "Геймплейные функции", "body": "- Цепочки квестов: «Пустоши», «Боевые операции», «Клан Альдекальдо», «Свобода».\\n- Сервисы: транспортная поддержка, тактическое прикрытие, доступ к выживанию.\\n- Навыки: высокий бой и вождение, средние технические навыки, низкие хакерские.\\n", "mechanics_links": ["mechanics/world/events/world-events-2040-2060.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "План основных квестов", "link": "shared/docs/knowledge/canon/narrative/quests/main-quests-outline.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертация профиля Панам Палмер в YAML и определение пустошных механик."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

