INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-kaede-ishikawa', 'Kaede Ishikawa — двойной агент Helios/Specter', '{"metadata": {"id": "canon-npc-kaede-ishikawa", "title": "Kaede Ishikawa — двойной агент Helios/Specter", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-08T00:25:00+00:00", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "double-agent", "helios", "specter"], "topics": ["narrative", "espionage"], "related_systems": ["narrative-service", "world-service", "social-service"], "related_documents": [{"id": "canon-quests-raid-2025-11-07-quest-helios-countermesh-conspiracy", "relation": "references"}, {"id": "canon-quests-raid-2025-11-07-raid-specter-surge", "relation": "influences"}, {"id": "canon-dialogues-npc-kaede-ishikawa", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/npc-kaede-ishikawa.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "Specter Narrative Cell", "reviewed_at": "2025-11-08T00:25:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Детализированный профиль Каэдэ Исикавы находился в Markdown и требовал интеграции в структурированную базу знаний.", "goal": "Зафиксировать биографию, мотивации и игровые триггеры двойного агента, балансирующего между Helios и Specter HQ.", "essence": "Kaede — аналитик Countermesh, работающий на две стороны, чьи решения зависят от репутации игрока и глобальных кризисов City Unrest.", "key_points": ["Поддерживает контакт с Kaori Watanabe и Dr. Lysander, фильтруя данные для обеих фракций.", "Управляет набором флагов лояльности и семейной угрозы, активируя миссии эвакуации и нейтральные контракты.", "Интегрирована с narrative API и эвентами `NPC_KAEDE_LoyaltyChanged`."]}, "content": {"sections": [{"id": "profile", "title": "Общая информация", "body": "Каэдэ Исикава — 32-летний бывший аналитик Helios Throne, завербованный Specter HQ и действующий как двойной агент, поставляющий\\nданные о Countermesh Ops и влияющий на показатели City Unrest.\\n", "mechanics_links": [], "assets": []}, {"id": "biography", "title": "Биография", "body": "Родилась в Neo-Kyoto, окончила Helios Tactical Institute, специализировалась на Countermesh сетях и фильтрах Blackwall.\\nПосле катаклизма 2073 участвовала в стабилизации Underlink, но столкновение с последствиями операций Helios привело к смене\\nлояльности и сотрудничеству с Kaori Watanabe.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивации", "body": "Главная мотивация — удержать баланс между корпорациями и гражданскими и предотвратить новый Cataclysm. Дополнительная цель —\\nзащитить экипаж брата (`flag.kaede.family`). Конфликт возникает при необходимости лгать обеим сторонам, чтобы минимизировать потери.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение и связи", "body": "Поддерживает зашифрованный канал с Kaori Watanabe и отчитывается перед Dr. Lysander, фильтруя данные. Лояльность зависит от\\nрепутации: при высоком `specter-prestige` склоняется к Specter, при `rep.corp.helios ≥ 25` — к Helios. Может активировать Intel Board\\nмиссии или Helios Ops по выбору игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "triggers", "title": "Триггеры и флаги", "body": "Используются флаги `flag.kaede.loyalty` (`specter`, `helios`, `balanced`), `flag.kaede.family-threatened` при кризисе Cataclysm и\\n`flag.kaede.betrayal` при невыполненных обещаниях Helios. Эти условия открывают побочные миссии и изменяют доступность контрактов.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Narrative Hooks", "body": "Миссии `Deep Cover`, `Split Allegiance`, кризис `City Unrest` и апгрейд Specter HQ Tier 3 используют Каэдэ как медиатора и источник\\nаналитических панелей. Она может предложить эвент «Evacuate Family» в PvE-сценариях.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая интеграция", "body": "При союзе со Specter снижает стоимость `intel-countermesh` на 15%, с Helios повышает шанс дропа `helios-drone-chip`, при балансе\\nоткрывает смешанные `neutral contracts`. Может появляться как информатор на Guild Contract Board.\\n", "mechanics_links": [], "assets": []}, {"id": "api", "title": "API и телеметрия", "body": "narrative-service предоставляет endpoints `GET/POST /api/v1/narrative/npc/kaede`; события `NPC_KAEDE_LoyaltyChanged` и\\n`NPC_KAEDE_Request` фиксируются телеметрией `kaede_interaction`, `kaede_loyalty_distribution`.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Helios Countermesh Conspiracy", "link": "../../quests/raid/2025-11-07-quest-helios-countermesh-conspiracy.md"}, {"title": "Raid Specter Surge", "link": "../../quests/raid/2025-11-07-raid-specter-surge.md"}, {"title": "Диалоги Каэдэ Исикавы", "link": "../dialogues/npc-kaede-ishikawa.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-08", "author": "narrative_team", "changes": "Создан профиль Каэдэ Исикавы, двойного агента Helios/Specter."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

