INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-rebecca', 'Rebecca — стрелок Edgerunners', '{"metadata": {"id": "canon-npc-rebecca", "title": "Rebecca — стрелок Edgerunners", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "edgerunners", "shooter"], "topics": ["narrative", "combat"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-npc-maine", "relation": "complements"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/rebecca.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль стрелка Rebecca был в Markdown и требовал включения в YAML базу NPC.", "goal": "Описать вспыльчивую стрелка команды Edgerunners с сильными синергиями в бою.", "essence": "Rebecca — агрессивная и верная боец, обеспечивающая высокий урон и поддерживающая тактику Мейна и Дэвида.", "key_points": ["Использует агрессивные билды с мощным огневым воздействием.", "Синергирует с Maine и David, усиливая командные стратегии.", "Имеет импульсивный характер и действует первой."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Rebecca — стрелок команды Edgerunners, известная агрессивным стилем, преданностью и готовностью защищать своих союзников\\nлюбой ценой.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Её участие усиливает агрессивные билды, повышает урон команды и добавляет синергии с Maine и David, особенно в скоростных рейдах.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"Стреляй первой. Вопросы — потом.\\" — реплика подчёркивает импульсивность и прямолинейность Rebecca.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Edgerunners Squad Notes", "link": "../edgerunners/overview.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-05", "author": "npc_team", "changes": "Создан профиль Rebecca."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

