INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-location-claire-2050-2090', 'Claire Russell — бартендер Afterlife', '{"metadata": {"id": "canon-npc-location-claire-2050-2090", "title": "Claire Russell — бартендер Afterlife", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:58:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "afterlife", "bartender"], "topics": ["narrative", "locations"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-scenarios-locations-night-city-afterlife-bar-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/locations/claire-afterlife-bartender-2050-2090.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Narrative Locations Cell", "reviewed_at": "2025-11-06T11:58:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Профиль бартендера Afterlife находился в Markdown и требовал переноса в YAML.", "goal": "Зафиксировать историю и роль Клэр Рассел как хранительницы легенд и персонажа локации Afterlife.", "essence": "Claire — трансгендерная женщина, которая обслуживает легендарных наёмников, хранит их истории и помогает игрокам разобраться с местью и потерями.", "key_points": ["Работает в Afterlife более 40 лет, помнит истории величайших соло.", "Путь мести за мужа Dean отражён в квесте «Гонка для Claire».", "Оказывает моральную поддержку V и другим посетителям бара."]}, "content": {"sections": [{"id": "history", "title": "История", "body": "Claire Russell управляет баром Afterlife, будучи свидетелем множества легенд. Потеряв мужа в гонке, она попыталась отомстить,\\nно осознала, что месть не возвращает любимых. Теперь помогает другим находить свой путь.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Добрая и мудрая «мать» Afterlife, помнящая каждого посетителя. Она сочувствует потерявшим близких и делится историями о прошлом.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квесты", "body": "«Гонка для Claire» — канонический квест CP2077, где игрок решает, помочь ли в мести или удержать Клэр от убийства, влияя на её\\nдальнейший путь.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"Afterlife — это место, где легенды пьют вместе с мертвецами. И я помню их всех.\\" — реплика подчёркивает её связь с историей бара.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Afterlife Bar 2020-2093", "link": "../../../../scenarios/locations/night-city/afterlife-bar-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "npc_team", "changes": "Создан профиль Claire Russell."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

